$breakpoint-ms: 720px;
$breakpoint-m: 960px;
$breakpoint-l: 1060px;
$breakpoint-xl: 1280px;

// -- Layout - #derived -- //
$gutter-third: $gutter / 3;
$gutter-half: $gutter / 2;
$gutter-double: $gutter * 2;

// -- Transitions - #set -- //
$ease-out-quad: cubic-bezier(0.250, 0.460, 0.450, 0.940);

//------ Layout Components ------//
