//-- Clearfix --//
// - You're not supposed to @extend from within mixins
// - You can't @extend for a media query range
@mixin clearfix {
  *zoom: 1;

  &:before,
  &:after {
    display: table;
    content: "";
  }

  &:after {
    clear: both;
  }
}

//-- Alert Mixin --//
@mixin alert($type: "info") {
  $alert-color: '';
  $dismiss-color: '';

  @if ($type == "error") {
    $alert-color: $error-color;
  } @else if ($type == "success") {
    $alert-color: $success-color;
  } @else {
    $alert-color: $info-color;
  }

  $background: $alert-color;
  $dismiss-color: mix($background, invert($alert-foreground), 75%);
  $foreground: $alert-foreground;

  background-color: $background;
  color: $foreground;
  transition: all 0.5s $ease-out-quad;

  .alert-message {
    a {
      @include anchor-underline("off-hover", $foreground, $foreground);
    }
  }

  .alert-dismiss {
    @include anchor-underline("none", $dismiss-color, brightness-adjust($dismiss-color));
    cursor: pointer;

    use {
      transition: all 0.5s $ease-out-quad;
      fill: $dismiss-color;
    }

    &:hover {

      use {
        fill: brightness-adjust($dismiss-color, false, 50%);
      }
    }
  }
}

@mixin button-with-arrow($color: $primary-button-color) {
  position: relative;
  padding-right: $gutter + $gutter-half;

  &:after {
    position: absolute;
    top: 50%;
    right: $gutter-half;
    display: inline-block;
    margin-top: - 0.2em;
    border-top: 0.3em solid transparent;
    border-bottom: 0.3em solid transparent;
    border-left: 0.3em solid $color;
    content: "";
  }
}

/* scss-combine:lib/extends */
//-- General --//
%header-box-shadow {
 box-shadow: 0 2px 3px rgba(contrast($header-background, 50%), 0.25);
}

%overlay {
  position: fixed;
  top: 0;
  right: 0;
  z-index: map-get($zindex, main-overlay);
  display: none;
  width: 100%;
  height: 100vh;
  opacity: 0;
  visibility: hidden;
  transition: all 0.5s $ease-out-quad;

  &.visible {
    display: block;
    opacity: 1;
    visibility: visible;
  }
}

%section-title-text-treatment {
  @extend %as-h5;
  letter-spacing: em(1px, $h5-font-base);
  color: $heading-color;
  text-transform: uppercase;
}

%meta-text-treatment {
  font-family: $meta-font;
  letter-spacing: 0.05em;
  color: $meta-color;
  text-transform: uppercase;
}

%wrapper {
  max-width: $max-width;
  padding-right: $gutter;
  padding-left: $gutter;
  margin-right: auto;
  margin-left: auto;

  @include breakpoint(s-down) {
    padding-right: $gutter-half;
    padding-left: $gutter-half;
  }
  // Solves border-box issue with max-width in IE8
  .lt-ie9 & {
    width: $max-width;
  }
}

%vertical-padding {
  padding-top: $gutter;
  padding-bottom: $gutter;

  @include breakpoint(s-down) {
    padding-top: $gutter-half;
    padding-bottom: $gutter-half;
  }
}

%full-width {
  padding-right: $gutter / 2;
  padding-left: $gutter / 2;
}

%show-for-sr {
  // scss-lint:disable ImportantRule
  position: absolute !important;
  width: 1px;
  height: 1px;
  overflow: hidden;
  clip: rect(1px, 1px, 1px, 1px);
}

%checkbox-hide {
  position: absolute;
  z-index: 1;
  width: 100%;
  height: 100%;
  cursor: pointer;
  opacity: 0;
}

%button-reset {
  padding: 0;
  background: transparent;
  border: 0;
  appearance: normal;

  &:focus {
    outline: none;
  }
}

//------ Typography ------//
//-- Font Groupings --//

%primary-font {
  font-family: get-font-family("primary-font");
}

%primary-font-normal {
  font-weight: get-font-weight("primary-font", "normal");
}

%primary-font-bold {
  font-weight: get-font-weight("primary-font", "bold");
}

%secondary-font {
  font-family: get-font-family("secondary-font");
}

%secondary-font-normal {
  font-weight: get-font-weight("secondary-font", "normal");
}

%secondary-font-bold {
  font-weight: get-font-weight("secondary-font", "bold");
}

%tertiary-font {
  font-family: get-font-family("tertiary-font");
}

%as-h1 {
  //always uses `secondary-font`
  @extend %secondary-font;
  margin-top: 0.67em;
  margin-bottom: 0.67em;
  font-size: $h1-font-size;
  font-weight: get-font-weight("secondary-font", $h1-font-weight);
}

%as-h2 {
  //always uses `secondary-font`
  @extend %secondary-font;
  margin-top: 0.83em;
  margin-bottom: 0.83em;
  font-size: $h2-font-size;
  font-weight: get-font-weight("secondary-font", $h2-font-weight);
}

%as-h3 {
  //always uses `primary-font`
  @extend %primary-font;
  margin-top: 1em;
  margin-bottom: 1em;
  font-size: $h3-font-size;
  font-weight: get-font-weight("primary-font", $h3-font-weight);
}

%as-h4 {
  //always uses `primary-font`
  @extend %secondary-font, %secondary-font-normal;
  margin-top: 1em;
  margin-bottom: 1em;
  font-size: $h4-font-size;
}

%as-h5 {
  //always uses `primary-font`
  @extend %primary-font, %primary-font-bold;
  margin-top: 1em;
  margin-bottom: 1em;
  font-size: $h5-font-size;
  letter-spacing: 0.02em;
}

%as-h6 {
  //always uses `secondary-font`
  @extend %secondary-font, %secondary-font-normal;
  margin-top: 1em;
  margin-bottom: 1em;
  font-size: $h6-font-size;
}

//-- Smoothing --//
%subpixel-smooth {
  -webkit-font-smoothing: subpixel-antialiased;
}

%font-smoothing {
  -moz-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  -webkit-font-smoothing: antialiased;
}

//-- Button Extends allow for re-application to other dom elements --//
%button-base {
  @extend %font-smoothing;
  padding: $button-padding-vertical $button-padding-horizontal;
  font-weight: $button-weight;
  letter-spacing: em(2px, 12px);
  text-transform: uppercase;
  border-style: solid;
  border-width: 1px;
  transition: all 0.2s $ease-out-quad;
}

%button-primary {
  color: $primary-button-color;
  background-color: $primary-button-background;
  border-color: $primary-button-border;

  &:hover {
    color: $primary-button-accent-color;
    background-color: $primary-button-accent-background;
    border-color: $primary-button-accent-border;
  }
}

%button-secondary {
  color: $secondary-button-color;
  background-color: $secondary-button-background;
  border-color: $secondary-button-border;

  &:hover {
    color: $secondary-button-accent-color;
    background-color: $secondary-button-accent-background;
    border-color: $secondary-button-accent-border;
  }
}

%button-secondary-inverse {
  color: $secondary-button-accent-color;
  background-color: $secondary-button-accent-background;
  border-color: $secondary-button-accent-border;

  &:hover {
    color: $secondary-button-accent-color;
    background-color: brightness-adjust($secondary-button-accent-background);
    border-color: brightness-adjust($secondary-button-accent-background);
  }
}

%button-large {
  padding: ($button-padding-vertical * 2) $button-padding-horizontal;
}

%button-small {
  padding: ($button-padding-vertical / 2) ($button-padding-horizontal / 2);
  font-size: em(12px, $body-font-size);
}

%primary-link-style {
  @include smallcaps;
  @include anchor-underline("on-hover", $heading-color, $heading-color);
  font-size: em(12px, $body-font-size);
  letter-spacing: em(1px, $body-font-size);
  transition: all 0.4s $ease-out-quad;
}

%secondary-link-style {
  @include smallcaps;
  @include anchor-underline("on-hover", $accent-color, $accent-color);
  font-size: em(12px, $body-font-size);
  font-weight: get-font-weight($weight: "medium");
  letter-spacing: em(1px, $body-font-size);
  transition: all 0.4s $ease-out-quad;
}

//-- Tabs --//
%tab-link-active {
  color: $tab-color-active;
  border-color: $tab-color-active;
}

%tab-link {
  @include smallcaps();
  display: block;
  padding-top: $tab-vertical-padding;
  padding-bottom: $tab-vertical-padding - 2px;
  font-size: em(12, $body-font-size);
  font-weight: get-font-weight($weight: "medium");
  letter-spacing: em(2px, $body-font-size);
  color: $tab-color;
  border-bottom: 2px solid transparent;
  transition: all 0.5s $ease-out-quad;

  &:hover {
    @extend %tab-link-active;
  }
}

//-- Collections --//
%collection-header-list {
  text-align: center;

  li {
    display: inline-block;
  }
}

%collection-header-list-item {
  @extend %secondary-link-style;
  font-weight: get-font-weight($weight: "bold");
  opacity: 0.5;
  transition: all 0.4s $ease-out-quad;
}

%collection-header-list-item-accent {
  color: $heading-color;
  opacity: 1;
}

//-- Backgrounds --//
%background-body {
  background-color: $body-background;
}

%background-content {
  background-color: $content-background;
}

%background-triangle-upper-left {
  @extend %background-content;
  @include diagonal-background($body-background, upper-left);
}

%background-triangle-upper-right {
  @extend %background-content;
  @include diagonal-background($body-background, upper-right);
}

%background-triangle-bottom-left {
  @extend %background-content;
  @include diagonal-background($body-background, bottom-left);
}

%background-triangle-bottom-right {
  @extend %background-content;
  @include diagonal-background($body-background, bottom-right);
}

//-- Product Items --//
%product-item {
  @include span(full);
  margin-top: gutter();
  background-color: $product-item-background;

  &:first-child {
    margin-top: 0;
  }
}

%product-item-image {
  position: relative;
  overflow: hidden;

  .item-thumbnail {
    @include aspect-ratio(map-get($product-item-image, width) map-get($product-item-image, height));
    display: block;
    overflow: hidden;
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: $product-item-image-crop;
    transform: scale(1, 1);
    transition: all 0.5s $ease-out-quad;

    a {
      display: block;
    }
  }

  .item-overlay {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    display: block;
    background: $content-background-transparent;
    opacity: 0;
    transition: all 0.5s $ease-out-quad;

    .no-js & {
      display: none;
    }
  }

  &:hover {

    .item-thumbnail {
      transform: scale(1.1, 1.1);
    }

    .item-overlay {
      opacity: 1;
    }

    .item-badges {
      opacity: 0;
    }

    .item-overlay-button {
      top: 50%;
      opacity: 1;
    }

    .item-overlay-remove {
      top: auto;
      bottom: 0;
    }
  }
}

%item-overlay-button {
  //scss-lint:disable NameFormat
  @extend %subpixel-smooth;
  position: absolute;
  top: 54%;
  left: 50%;
  width: 160px;
  padding: 0 $gutter-half;
  margin-left: -80px;
  text-align: center;
  opacity: 0;
  transition: all 0.5s $ease-out-quad;

  @if $alternate-product-item-size {
    height: 36px;
    margin-top: -18px;
    line-height: 34px;
  } @else {
    height: 56px;
    margin-top: -28px;
    line-height: 54px;
  }

  &:hover {
    color: $product-item-quick-view-color;
    background-color: contrast($product-item-quick-view-background, 20%);
  }

  &.item-overlay-remove {
    top: auto;
    bottom: -4%;
  }
}

%product-item-title {
  @extend %as-h3, %primary-font, %primary-font-normal;
  @include smallcaps;
  margin-top: 0;
  margin-bottom: em(8px, 18);
  font-size: em(14px);
  line-height: 1.2;

  a {
    color: $heading-color;
  }
}

%product-item-badges {
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
  display: block;
  transition: all 0.5s $ease-out-quad;

  .badge-out-of-stock,
  .badge-sale-tag {
    @extend %subpixel-smooth;
    @extend %meta-text-treatment;
    display: inline-block;
    padding: 7px 10px;
    font-size: em(10, 14);
    color: $product-item-sale-color;
    pointer-events: none;
    background-color: $product-item-sale-background;
  }

  .badge-out-of-stock {
    color: $product-item-sold-out-color;
    background-color: $product-item-sold-out-background;
  }

  .badge-sale-tag {
    float: right;
  }
}

%product-item-grid-3 {
  @include breakpoint(s) {
    @include gallery(6 of 12);

    &:nth-child(-n+2) {
      margin-top: 0;
    }
  }

  @include breakpoint(m) {
    @include gallery(4 of 12);

    &:nth-child(-n+3) {
      margin-top: 0;
    }
  }
}

%product-item-grid-4 {
  @include breakpoint(s) {
    @include gallery(6 of 12);

    &:nth-child(-n+2) {
      margin-top: 0;
    }
  }

  @include breakpoint(m) {
    @include gallery(3 of 12);

    &:nth-child(-n+4) {
      margin-top: 0;
    }
  }
}


// Module overrides
/* scss-combine:modules/flickity */
// *! Flickity v1.1.1
// http://flickity.metafizzy.co

.flickity-enabled {
  position: relative;

  &.is-draggable {
    user-select: none;
    tap-highlight-color: transparent;

    .flickity-viewport {
      //scss-lint:disable DuplicateProperty, NestingDepth
      cursor: move;
      cursor: grab;

      &.is-pointer-down { cursor: grabbing; }
    }
  }

  &:focus { outline: none; }
}

.flickity-viewport {
  position: relative;
  height: 100%;
  overflow: hidden;
}

.flickity-slider {
  position: absolute;
  width: 100%;
  height: 100%;
}

.flickity-prev-next-button {
  position: absolute;
  top: $gutter-half;
  z-index: 2;
  display: none;
  width: $carousel-arrow-size / 1.5;
  height: $carousel-arrow-size / 1.5;
  margin-top: 0;
  background-color: transparent;
  border: 2px solid rgba($carousel-title-color, 0.4);
  border-radius: 50%;
  transition: all 0.3s $ease-out-quad;

  @include breakpoint(m) {
    top: 50%;
    width: $carousel-arrow-size;
    height: $carousel-arrow-size;
    margin-top: -($carousel-arrow-size / 2);
  }

  .display-controls & {
    display: block;
  }

  &:hover {
    cursor: pointer;
    background-color: rgba($carousel-button-background, 0.25);
    border-color: $carousel-title-color;

    svg {
      fill: $carousel-title-color;
    }
  }

  &.previous {
    left: $gutter-half;

    @include breakpoint(m) {
      left: $gutter;
    }
  }

  &.next {
    right: $gutter-half;

    @include breakpoint(m) {
      right: $gutter;
    }
  }

  &:focus {
    outline: none;
  }


  &:disabled {
    cursor: auto;
  }

  svg {
    @include centerer();
    width: $carousel-arrow-size / 5;
    height: $carousel-arrow-size / 5;
    transition: all 0.4s $ease-out-quad;
    fill: rgba($carousel-title-color, 0.4);

    @include breakpoint(m) {
      width: $carousel-arrow-size / 4;
      height: $carousel-arrow-size / 4;
    }
  }
}

/* scss-combine:modules/baguettebox */
/*!
 * baguetteBox.js
 * @author  feimosi
 * @version 1.4.1
 * @url https://github.com/feimosi/baguetteBox.js
 */

// scss-lint:disable QualifyingElement, IdSelector, SelectorFormat

%arrow-button {
  top: calc(50% - 30px);
  width: 44px;
  height: 60px;
}

#baguetteBox-overlay {
  @extend %overlay;
  z-index: map-get($zindex, zoom-modal);
  background-color: $modal-background-color;

  .full-image {
    position: relative;
    display: inline-block;
    width: 100%;
    height: 100%;
    text-align: center;

    figure {
      display: inline;
      height: 100%; // Opera 12 image stretching fix
      margin: 0;
    }

    img {
      display: inline-block;
      width: auto;
      height: auto;
      max-width: 100%;
      max-height: calc(100% - #{$gutter * 3});
      vertical-align: middle;
    }

    figcaption {
      position: absolute;
      bottom: 0;
      display: block;
      width: 100%;
      padding-bottom: 16px;
      font-weight: 300;
      line-height: 1.8;
      color: $content-background;
      text-align: center;
      white-space: normal;
    }

    &:before {
      display: inline-block;
      width: 1px;
      height: 50%;
      margin-right: -1px;
      content: "";
    }
  }
}

#baguetteBox-slider {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  white-space: nowrap;
  transition: left 0.4s ease, transform 0.4s ease;

  &.bounce-from-right {
    animation: bounceFromRight 0.4s ease-out;
  }

  &.bounce-from-left {
    animation: bounceFromLeft 0.4s ease-out;
  }
}

@keyframes bounceFromRight {
  0% { margin-left: 0; }
  50% { margin-left: -30px; }
  100% { margin-left: 0; }
}

@keyframes bounceFromLeft {
  0% { margin-left: 0; }
  50% { margin-left: 30px; }
  100% { margin-left: 0; }
}

.baguetteBox-button {
  position: absolute;
  padding: 0;
  margin: 0;
  font-size: $body-font-size;
  color: $modal-accent-color;
  cursor: pointer;
  background-color: transparent;
  border: 0;
  border-radius: 15%;
  outline: none;
  transition: background-color 0.4s ease;

  &:hover {
    color: $modal-accent-color-active;
  }

  &#next-button {
    @extend %arrow-button;
    right: 2%;
  }

  &#previous-button {
    @extend %arrow-button;
    left: 2%;
  }

  &#close-button {
    @include icon-close-thin($fill-color: $modal-accent-color);
    top: $gutter-half;
    right: $gutter-half;
    width: 24px;
    height: 24px;
    padding: 0px;

    &:hover {
      @include icon-close-thin($fill-color: $modal-accent-color-active);
    }

    svg { visibility: hidden;}
  }

  // Firefox fix
  svg {
    position: absolute;
    top: 0;
    left: 0;
  }
}

// http://tobiasahlin.com/spinkit/
.spinner {
  position: absolute;
  top: 50%;
  left: 50%;
  display: inline-block;
  width: 64px;
  height: 64px;
  margin-top: -32px;
  margin-left: -32px;
}

.double-bounce1,
.double-bounce2 {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: $content-background;
  border-radius: 50%;
  opacity: 0.6;
  animation: bounce 2s infinite ease-in-out;
}

.double-bounce2 { animation-delay: -1s; }

@keyframes bounce {
  0%, 100% { transform: scale(0); }

  50% { transform: scale(1); }
}

/* scss-combine:modules/carousel */
// Carousel
.carousel {
  position: relative;
  width: 100%;
  background-color: $content-background;

  img {
    display: none;
  }

  *:focus {
    outline: none;
  }
}

.flickity-viewport {
  height: 0;
  overflow: hidden;
  transition: height 0.4s $ease-out-quad, max-height 0.4s $ease-out-quad;

  img {
    display: block;
  }
}

// Carousel Item
.carousel-item {
  position: relative;
  display: block;
  width: 100%;
  transition: transform 0.4s $ease-out-quad;

  &.is-selected {
    //scss-lint:disable NestingDepth
    + .carousel-item {
      height: 100%;

      img,
      .carousel-item-image {
        height: 100%;
      }
    }
  }

  figure,
  img {
    width: 100%;
  }
}

// Carousel Text
.carousel-item-info {
  position: relative;
  top: auto;
  left: auto;
  width: 100%;
  padding: 0 10%;
  font-family: $carousel-font;
  text-align: $carousel-text-alignment;
  opacity: 0;
  transition: opacity 0.3s $ease-out-quad;

  @include breakpoint(ms) {
    @include centerer;
    width: percentage(2 / 3);
    padding: 0;
  }

  .is-selected & {
    opacity: 1;
  }

  *:first-child {
    margin-top: 0;

    @include breakpoint(ms-only) {
      margin-top: $gutter;
    }
  }

  *:last-child {
    margin-bottom: 0;
  }

  .carousel-title-background & {
    padding: $gutter-double $gutter;
    background-color: rgba($carousel-title-background, 0.8);
  }
}

.carousel-item-heading {
  @extend %as-h1;
  margin: $gutter-half 0;
  font-size: em(18px, $body-font-size);
  font-weight: $carousel-font-weight;
  color: $carousel-title-color;
  transition: all 0.3s $ease-out-quad;

  @include breakpoint(s) {
    font-size: $carousel-title-size / 2;
  }

  @include breakpoint(m) {
    font-size: $carousel-title-size / 1.66;
  }

  @include breakpoint(l) {
    font-size: $carousel-title-size / 1.33;
  }

  @include breakpoint(xl) {
    font-size: $carousel-title-size;
  }
}

.carousel-item-description {
  max-width: 100%;
  margin: 15px 0;
  font-family: inherit;
  font-size: $body-font-size;
  color: $carousel-title-color;
  transition: all 0.3s $ease-out-quad;

  @include breakpoint(s) {
    font-size: $carousel-caption-size / 1.33;
  }

  @include breakpoint(l) {
    font-size: $carousel-caption-size;
  }
}

.carousel-item-button {
  @include breakpoint(s) {
    margin: 15px 0;
  }

  @include breakpoint(l) {
    //TODO: Ensure this is added to the extends
    @extend %button-large;
  }
}

.carousel-slides {
  ~ * {
    display: none;
  }

  &.flickity-enabled ~ * {
    display: block;
  }
}

// Pagination
.flickity-page-dots {
  position: absolute;
  bottom: 15px;
  z-index: 2;
  display: none;
  width: 100%;
  padding: 0;
  text-align: center;

  @include breakpoint(xl) {
    bottom: 60px;
  }

  .display-controls & {
    display: block;
  }

  .dot {
    display: inline-block;
    width: 10px;
    height: 10px;
    margin: 5px;
    cursor: pointer;
    background-color: rgba($carousel-title-color, 0.3);
    transform: rotate(45deg);

    @include breakpoint(l) {
      width: 16px;
      height: 16px;
      margin: 8px;
    }

    &:hover,
    &.is-selected {
      transform: rotate(135deg);
      transition: transform 0.7s $ease-out-quad;
    }

    &.is-selected {
      background-color: $carousel-title-color;
    }
  }
}

// Optional Fade Transition
.fade-transition {
  .flickity-slider {
    background-color: $content-background;
    transform: none !important; //scss-lint:disable ImportantRule
  }

  .carousel-item {
    left: 0 !important; //scss-lint:disable ImportantRule
    background-color: $content-background;
    opacity: 0;
    transition: opacity 0.6s $ease-out-quad;

    &.is-selected {
      z-index: 1;
      opacity: 1;
    }
  }
}

/* scss-combine:modules/tabs */

.tabs {
  border-bottom: 1px solid $content-border-color;
}

.tab-title {
  display: inline-block;
  margin-left: $gutter-half;
  font-weight: normal;

  @include breakpoint(l) {
    &:first-child {
      margin-left: 0;
    }  
  }

  .tab-link {
    @extend %tab-link;
  }

  &.active .tab-link {
    @extend %tab-link-active;
  }
}

.tab-content-panel {
  display: none;

  &.active {
    display: block;
  }
}

.tab-loading {
  position: relative;
  width: 100%;
  min-height: 300px;
}

/* scss-combine:modules/core-extends */

%account-nav {
  padding: 0;
  font-size: em(12, $body-font-size);
  list-style: none;
  border-width: 0;
  border-radius: $account-border-radius;

  a {
    @include smallcaps($font-size: 12);
    padding: 0;
    font-weight: get-font-weight($weight: "medium");
    color: $account-nav-link;

    &:hover { color: $account-nav-link-active; }
  }

  li {
    margin-top: $gutter-half;
    line-height: 1.3;
    border: 0;

    &:first-child { margin-top: 0; }

    &.is-active {
      border-left: 0;

      a { color: $account-nav-link-active; }
    }
  }

}

%account-layout-nav {
  @include span(12 first);
  padding-right: 0;
  padding-bottom: em($gutter-double);

  @include breakpoint(ms) {
    @include span(9 of 12);
    float: none;
    padding-bottom: em($gutter);
    margin-right: auto;
    margin-left: auto;
  }

  @include breakpoint(m) {
    @include span(3 of 12 first);
    padding-right: em($gutter-half);
    padding-bottom: 0;
  }

  .alert {
    margin-top: $gutter;
  }
}

%account-layout-body {
  @include span(12 first);
  padding-left: 0;

  @include breakpoint(ms) {
    @include span(9 of 12);
    float: none;
    margin-right: auto;
    margin-left: auto;
  }

  @include breakpoint(m) {
    @include span(9 of 12);
    padding-bottom: 0;

    .account-nav-hidden & {
      float: none;
      margin-right: auto;
      margin-left: auto;
    }
  }
}


// General
/* scss-combine:general/icons */
.icon-sprite {
  display: none;
}

.icon-increment-vertical {
  transition: all 0.4s $ease-out-quad;
  transform-origin: 50% 50%;
}

/* scss-combine:general/layout */
.page-content {
  @extend %clearfix;
}

.layout-container {
  @extend %wrapper;

  &.nested {
    padding-right: 0;
    padding-left: 0;
  }
}

//-- Backgrounds --//
.background-body {
  @extend %background-body;
}

.background-content {
  @extend %background-content;
}

.background-triangle-upper-left {
  @extend %background-triangle-upper-left;
}

.background-triangle-upper-right {
  @extend %background-triangle-upper-right;
}

.background-triangle-bottom-left {
  @extend %background-triangle-bottom-left;
}

.background-triangle-bottom-right {
  @extend %background-triangle-bottom-right;
}

/* scss-combine:general/general */
//-- General --//

body {
  font-family: $body-font;
  font-size: $body-font-size;
  line-height: 1.666;
  color: $body-color;
  background-color: $body-background;

  &.scroll-locked {
    overflow: hidden;
  }
}

//-- Links --//

a {
  color: $accent-color;
  transition: color 0.2s $ease-out-quad;

  &:hover {
    @include hover($accent-color);
  }
}

//-- Headers --//

h1,
h2,
h3,
h4,
h5,
h6 {
  color: $heading-color;
  line-height: 1.25;
}

h1 {
  @extend %as-h1;
}


h2 {
  @extend %as-h2;
}

h3 {
  @extend %as-h3;
}

h4 {
  @extend %as-h4;
}

h5 {
  @extend %as-h5;
}

h6 {
  @extend %as-h6;
}

.page-title {
  @extend %as-h1;
  font-size: $h3-font-size;

  @include breakpoint(ms) {
    margin-top: 1em;
    margin-bottom: 1em;
    font-size: $h2-font-size;
  }

  @include breakpoint(l) {
    margin-top: 1.67em;
    margin-bottom: 1.67em;
    font-size: $h1-font-size;
  }

  .error-page  & {
    text-align: center;
  }
}

.section-title {
  @extend %section-title-text-treatment;
  margin-top: 0;
  text-align: center;

  + .section-sub-title {
    margin-bottom: em((28 * 0.75), 14);
  }
}

.section-sub-title {
  display: block;
  text-align: center;
}

//-- Basic elements --//

hr {
  background: $content-border-color;
}

cite {
  color: $meta-color;
}

.hidden {
  display: none;
}

[style*="no-image"],
[src*="no-image"],
.no-image {
  background-color: $body-background;
}

/* scss-combine:general/button */
//------ Buttons ------//
.button {
  @extend %button-base, %button-primary, %font-smoothing;
  font-size: em(12px, $body-font-size);

  &.button-wide {
    width: 100%;
  }

  &.button-large {
    @extend %button-large;
  }

  &.button-small {
    @extend %button-small;
  }

  &.secondary {
    @extend %button-secondary;
  }

  &.secondary-inverse {
    @extend %button-secondary-inverse;
  }

  &.button-disabled {
    color: $disabled-button-color;
    cursor: default;
    background: $disabled-button-background;
    border-color: $disabled-button-background;
  }
}

/* scss-combine:general/form */
//------ Forms ------//
.form-field {
  @extend %tertiary-font;

  //scss-lint:disable UnnecessaryParentReference
  .modal:not(.modal-quick-shop) & {
    & ~ .form-field {
      margin-top: $gutter-third;
    }
  }
}

.form-inline {
  .form-label-text,
  .form-field-title {
    display: inline-block;
    vertical-align: middle;
  }

  .form-input {
    display: inline-block;
  }
}

.page-form {
  .form-field {
    margin: $gutter-half 0;
  }
}

//-- Field Labels --//
.form-label-text,
.form-field-title {
  @extend %meta-text-treatment;
  display: block;
  margin-bottom: em($gutter-third);
  font-size: em(12px);
  font-weight: get-font-weight($weight: "bold");
  letter-spacing: 0.1em;

  .required-text {
    // Additional styles in place in case these should be shown later
    color: $body-color;
  }

  .modal:not(.modal-quick-shop) & {
    @extend %show-for-sr;
  }
}

.form-inline-message {
  @extend %meta-text-treatment;
  font-size: 0.8em;

  .form-field-invalid & {
    color: $error-color;
  }

  .form-field-valid & {
    color: $success-color;
  }
}

//-- Text Inputs --//
.form-input {
  display: block;
  width: 100%;
  min-height: $body-font-size + $gutter;
  padding: $gutter-third $gutter-half;
  line-height: inherit;
  color: $input-color;
  background: $input-background-color;
  border-color: $input-border-color;
  border-radius: 0;
  transition: border-color 0.2s $ease-out-quad;

  &::placeholder {
    color: lighten($input-color, 10%);
  }

  &:focus {
    outline: none;
  }

  .form-icon-inline & {
    padding-right: $body-font-size + $gutter-third * 2 + $gutter-half;
  }

  .form-field-invalid & {
    border-color: $error-color;
  }

  .form-field-valid & {
    border-color: $success-color;
  }
}

//-- Form Errors --//
.form-error {
  color: $error-color;
  border-color: $error-color;

  &::placeholder {
    color: lighten($error-color, 30%);
  }
}

.form-error-message {
  color: $error-color;
}

//-- Select Wrapper --//
.form-select-wrapper {
  position: relative;
  display: inline-block;
  width: 100%;
  min-height: $body-font-size + $gutter;
  padding: $gutter-third $gutter $gutter-third $gutter-half;
  font-size: $body-font-size;
  line-height: 1.6;
  color: $input-color;
  cursor: pointer;
  background-color: $input-background-color;
  border: 1px solid $input-border-color;

  @include breakpoint(l) {
    max-width: $input-max-width;
  }

  .modal:not(.modal-quick-shop) & {
    max-width: 100%;
  }

  //TODO: Establish a No-js fall back to this
  &:after {
    position: absolute;
    top: 50%;
    right: em($gutter-half);
    margin-top: -2px;
    border-top: 4px solid contrast($input-border-color, 50%);
    border-right: 4px solid transparent;
    border-left: 4px solid transparent;
    content: "";
  }

  select {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 2;
    width: 100%; // needed for Firefox
    height: 100%; // needed for IE
    cursor: pointer;
    opacity: 0;
    appearance: none;

    // For IE 10+
    &::-ms-expand { display: none; }
  }
}

//-- Checkboxes & Radios --//
.form-field-radio,
.form-field-checkbox {
  position: relative;

  .form-field-control {
    vertical-align: middle;
  }
}

.form-radio,
.form-checkbox {
  @extend %checkbox-hide;
  width: 0;

  + .form-label-text {
    margin-bottom: 0;
    vertical-align: middle;
    cursor: pointer;

    //scss-lint:disable ImportantRule, NestingDepth
    .modal:not(.modal-quick-shop) & {
      position: relative !important;
      width: auto;
      height: auto;
      overflow: visible;
      clip: inherit;
    }

    &:before {
      @include icon-unchecked($input-color);
      position: relative;
      top: 2px;
      display: inline-block;
      width: 1em;
      height: 1em;
      margin-right: $gutter-third;
      cursor: pointer;
      content: "";
    }

    &:hover {
      color: $body-color-alt;

      &:before {
        @include icon-checked($input-color, mix($input-color, $content-background, 50%));
      }
    }
  }

  &:checked + .form-label-text {
    color: $body-color-alt;

    &:before {
      @include icon-checked($input-color);
    }
  }
}

//-- Quantity Increment Components --//
.form-field-quantity {
  position: relative;
  display: block;
  width: 80px;

  .form-input {
    height: 55px;
    padding: 0 34px 0 5px;
    text-align: center;
    background-color: brightness-adjust($input-background-color, false, 5%);
    border: 0;

    .js & {
      -moz-appearance: textfield;
    }

    &::-webkit-outer-spin-button,
    &::-webkit-inner-spin-button {
      -webkit-appearance: none;
    }
  }
}

.quantity-control {
  position: absolute;
  top: 0;
  right: 0;
  z-index: 1;
  width: 30px;
  height: 29px;
  padding: 0;
  cursor: pointer;
  background-color: brightness-adjust($input-background-color, false, 13%);
  border: 2px solid $content-background;
  transition: all 0.3s $ease-out-quad;
  user-select: none;

  .no-js & {
    pointer-events: none;
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.2s $ease-out-quad;
  }

  &:hover {
    z-index: 10;
    background-color: $primary-button-background;

    use {
      fill: $primary-button-color;
    }
  }

  &.quantity-increment {
    z-index: 1;
    border-top: 0;
  }

  &.quantity-decrement {
    top: auto;
    bottom: 0;
    border-bottom: 0;
  }
}

.form-control-quantity {
  position: absolute;
  top: 50%;
  left: 50%;
  display: block;
  width: 10px;
  height: 10px;
  padding: 0;
  margin: -5px 0 0 -5px;

  use {
    transition: all 0.3s $ease-out-quad;
    fill: brightness-adjust($input-background-color, false, 50%);
  }

  &:hover {
    z-index: 10;
  }
}

//-- Form Icons --//
.form-icon-button {
  display: inline-block;
  width: 45px;
  min-height: 100%;
  background-color: $input-background-color;
  border: 1px solid $input-border-color;
  transition: background-color 0.3s $ease-out-quad;

  svg {
    position: absolute;
    top: 50%;
    left: 50%;
    display: inline-block;
    width: 14px;
    height: 14px;
    margin: -7px 0 0 -7px;
  }

  use {
    transition: fill 0.3s $ease-out-quad;
    fill: $input-color;
  }

  &:hover,
  &:active {
    background-color: contrast($input-background-color, 10%);

    use {
      fill: $input-border-color-accent;
    }
  }
}

//-- Form Captchas --//
.form-field-captcha {
  //scss-lint:disable NestingDepth, SelectorDepth
  float: left;
  width: 100%;

  @include breakpoint(m-only) {
    font-size: 0;

    .form-label,
    .captcha-note { font-size: $body-font-size; }
  }

  .captcha-image {
    display: inline-block;
    padding: em(5px);
    margin: em(15px) em(15px) em(15px) 0;
    vertical-align: middle;
    background-color: rgba($input-border-color, 0.5);
    border: 1px solid contrast($input-border-color, 15%);

    @include breakpoint(m-only) {
      width: 48%;
      margin-right: 4%;
    }

    img {
      display: block;
      height: em(75px);

      @include breakpoint(m-only) {
        width: 100%;
        height: auto;
      }
    }
  }

  .form-field-control {
    display: inline-block;
  }
}

/* scss-combine:general/table */
//-- Tables --//

table {
  color: $body-color;
  border-color: $content-border-color;
}

th {
  @extend %as-h6;
  color: $input-color;
  background: $input-background-color;
}

/* scss-combine:general/rte */
//------ Rich Text Editor ------//
.rte {
  @extend %clearfix;
  font-size: $body-font-size;
  text-align: left;

  > *:first-child {
    margin-top: 0;
  }

  > *:last-child {
    margin-bottom: 0;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    line-height: 1.5;
  }

  ul {
    list-style: disc;
  }

  ul,
  ol,
  dl {
    margin-left: $gutter;
  }

  blockquote {
    padding-left: $gutter;
    margin-left: 0;
    border-left: 2px solid $accent-color;
  }

  img {
    height: auto;
    max-width: 100%;
  }
}


// -- Components -- //
/* scss-combine:components/alerts */
.main-alert {
  opacity: 0;
  transition: opacity 0.5s $ease-out-quad;

  &.visible {
    opacity: 1;
  }
}

.alert {
  position: relative;
  height: auto;
  padding: $gutter-half $gutter * 2 $gutter-half $gutter-half;

  &,
  &.alert-message {
    transition: all 10s $ease-out-quad;
  }

  &.alert-error {
    @include alert("error");
  }

  &.alert-info {
    @include alert("info");
  }

  &.alert-success {
    @include alert("success");
  }

  &.dismissed {
    height: 0;
    padding: 0;

    .alert-message {
      opacity: 0;
    }
  }

  .modal-quick-shop & {
    transition: background-color 0.5s $ease-out-quad, color 0.5s $ease-out-quad; ;
  }
}

.alert-dismiss {
  @include anchor-underline("none");
  @include icon-close-thin();
  position: absolute;
  top: 50%;
  right: $gutter-half;
  display: inline-block;
  width: $gutter-half;
  font-size: $gutter;
  line-height: 1;
  text-indent: -9999px;
  background-size: contain;
  transform: translateY(-50%);
  transition: all 0.5s $ease-out-quad;

  .icon-dismiss,
  &.has-icon {
    width: $gutter * 2 / 3;
    height: 100%;
  }
}

/* scss-combine:components/banners */
.banner-wrapper-top {
  display: table-row;
  width: 100%;
  font-family: $banner-font;
  font-weight: $banner-weight;
  color: $banner-color;
  background-color: $banner-background;

  .mobile-nav-open &,
  .search-open & {
    display: none;
  }

  .container {
    display: table-cell;
    padding: $gutter / 2 $gutter;
    font-size: 115%;
    text-align: center;
  }

  a:not(.button),
  a {
    .main-header & {
      color: $banner-link-color;

      &:hover {
        color: brightness-adjust($banner-link-color);
      }
    }
  }
}

.banner-wrapper-bottom {
  @if($newsletter-background == $body-background){
    border-top: 1px solid $content-border-color;
  }

  + .footer-container {
    border-top-color: transparent;
  }

  .layout-container {
    padding-top: $gutter-double;
    padding-bottom: $gutter-double;
  }
}

/* scss-combine:components/blog/sidebar */
//-- Blog Sidebar --//
.blog-sidebar-heading {
  @extend %meta-text-treatment;
  padding: $blog-gutter;
  margin: 0;
  font-size: $h6-font-size;
  border-bottom: 1px solid $content-border-color;

  @include breakpoint(s-down) {
    &:first-child {
      border-top: 1px solid $content-border-color;
    }
  }
}

.blog-sidebar-component {
  padding: $blog-gutter;
  border-bottom: 1px solid $content-border-color;

  &:last-child {
    border-bottom: 0;
  }
}

.blog-sidebar-item {
  margin-top: $blog-gutter;

  &:first-child {
    margin-top: 0;
  }

  span {
    display: block;
    line-height: 1.4;
  }
}

.blog-sidebar-link-title {
  color: $blog-sidebar-link-title;
}

.blog-sidebar-meta {
  margin-top: 5px;
  color: $blog-sidebar-meta;
}

/* scss-combine:components/blog/blog-item */
.blog-item {
  margin-top: $gutter;
}

.blog-item-featured-image {
  @include aspect-ratio(12 7);
  display: block;
  background: 50% 50% $content-background no-repeat;
  background-size: cover;
}

.blog-item-title {
  @extend %as-h2;
  padding: $blog-gutter * 2 $blog-gutter * 2 $blog-gutter;
  margin: 0;
  text-align: center;

  @include breakpoint(s-down) {
    font-size: $h3-font-size;
  }

  a {
    color: $heading-color;
    transition: color 0.2s $ease-out-quad;

    &:hover {
      color: $body-color-alt;
    }
  }
}

.blog-item-header-caption {
  @include smallcaps;
  padding: 0 $blog-gutter;
  font-size: em(12px, $body-font-size);
  color: $meta-color;
  text-align: center;

  &:after {
    display: block;
    width: em(35px);
    margin: $blog-gutter auto 0;
    border-bottom: 1px solid $content-border-color;
    content: "";
  }
}

.blog-item-author {
  margin-left: $blog-gutter / 2;
}

.blog-item-tags {
  display: inline-block;
  text-align: left;

  .template-blog & {
    display: block;
    margin-bottom: $gutter;
    text-align: center;
  }
}

.blog-item-tags-label,
.blog-item-tags-list {
  //scss-
  @include smallcaps;
  display: inline-block;
  margin-left: 0 !important; //override RTE list styles
  color: $meta-color;
  vertical-align: middle;
}

.blog-item-tags-label {
  margin-right: $gutter-third;
}

.blog-item-tag {
  display: inline;
}

.blog-item-actions {
  margin-top: $blog-gutter;

  @include breakpoint(m) {
    margin-top: $blog-gutter * 2;
  }

  .template-blog & {
    text-align: center;
  }

  .share-buttons {
    margin-top: $blog-gutter;

    @include breakpoint(ms) {
      float: right;
      margin-top: 0;
    }
  }

  .share-buttons-list-trigger {
    position: relative;
    left: em(55px, $body-font-size) / 2;
    padding: 0;
    border: 0;

    @include breakpoint(ms) {
      left: 0;
      float: right;
    }

    &:hover {
      border: 0;

    }
  }

  .share-buttons-list {
    top: $gutter-third;
    margin-top: $gutter;
    margin-left: 0;
  }
}

/* scss-combine:components/cart/content */
//-- Initial Cell / Table Settings --//
.cart-item-header {
  display: none;

  .cart-item-cell {
    @include smallcaps;
    font-size: em(12px, $body-font-size);
    letter-spacing: em(2px, $body-font-size);
    color: $meta-color;
    text-align: right;

    &:first-of-type {
      text-align: left;
    }
  }
}

.cart-item-cell {
  width: 100%;
}

//-- Main Cart Area --//
.cart-item-row {
  border-bottom: 1px solid $content-border-color;

  @include breakpoint(ms) {
    @include layout-group(0, $gutter);
  }
}

//-- Cart Content Rows --//
.cart-item-thumbnail {
  float: left;
  width: map-get($cart-item-thumbnail, large);
  height: map-get($cart-item-thumbnail, large);
  margin-right: $gutter-half;
  vertical-align: middle;
  background-position: 50% 0;
  background-repeat: no-repeat;
  background-size: contain;

  @include breakpoint(ms-only) {
    width: map-get($cart-item-thumbnail, small);
    height: map-get($cart-item-thumbnail, small);
  }
}

.cart-item-info {
  float: left;
  width: calc(100% - #{$cart-item-info-offset-large});

  @include breakpoint(ms-only) {
    width: calc(100% - #{$cart-item-info-offset-small});
  }
}

.cart-item-brand {
  @extend %meta-text-treatment;
  display: block;
  margin-bottom: $gutter-third / 2;
  font-size: em(12px);
  text-transform: uppercase;
}

.cart-item-title {
  @include smallcaps;
  @include anchor-underline('on-hover', $heading-color, $heading-color);
  position: relative;
  top: em(-5px, $body-font-size); // account for line-height shift
  display: inline-block;
  max-width: 100%;
  transition: border 0.4s $ease-out-quad;

  &.no-link {
    border: 0;
  }
}

.cart-item-block {
  display: block;
}

.cart-item-accent {
  display: block;
  line-height: 1;

  &.price {
    font-size: em(16px, $body-font-size);
  }

  &.price-discounted {
    margin-bottom: em(8px, $body-font-size);
    text-decoration: line-through;
  }
}

//-- Cart Item Options --//
.cart-item-option-list {
  padding-top: em(5px, $body-font-size);
}

.cart-item-option {
  @include smallcaps;
  display: block;
  font-size: em(11px, $body-font-size);
  color: $meta-color;
}

.cart-item-option-value {
  font-weight: normal;
  color: $body-color-alt;
}

.cart-item-giftwrap {
  width: 100%;
}

//-- Cart Item Actions --//
.cart-item-action {
  text-align: right;
}

.cart-item-remove {
  display: inline-block;

  &:hover svg {
    fill: $heading-color;
  }

  svg {
    display: block;
    width: $cart-item-icon-size;
    height: $cart-item-icon-size;
    transition: fill 0.4s $ease-out-quad;
    fill: $body-color;
  }
}

/* scss-combine:components/cart/content-small */
//-- Cart Content -- Small Only --//
@include breakpoint(s-down) {
  .cart-item-row {
    @include clearfix();
    padding: $gutter $gutter-half;

    .button {
      display: block;
      padding: 5px;
      font-size: 1em;
    }
  }

  .cart-item-product {
    @include span(12 first);
    padding-bottom: $gutter-half;
  }

  .cart-item-quantity {
    @include span(6 first);
    padding-bottom: $gutter-half;
  }

  .cart-item-total {
    @include span(6);
    position: relative;
    padding-bottom: $gutter-half;
    font-size: $h4-font-size;
    text-align: right;

    @include breakpoint(s-down) {
      padding-bottom: 5px;
    }
  }

  .cart-item-price {
    display: none;
  }

  .cart-item-giftwrap {
    @include break();
    @include span(12 first);
  }

  .cart-item-action {
    @include span(6);
  }
}

/* scss-combine:components/cart/content-medium-small */
//-- Cart Content -- Medium-Small Up --//
@include breakpoint(ms) {

  .cart-item-cell {
    @include layout-group-item(top);
    text-align: right;
  }

  .cart-item-row .button {
    padding: 5px;
    font-size: em($body-font-size - 2);
  }

  .cart-item-product {
    width: percentage(map-get($cart-item-cell, product) / map-get($cart-item-cell, columns));
    text-align: left;
  }

  .cart-item-price {
    width: percentage(map-get($cart-item-cell, price) / map-get($cart-item-cell, columns));
  }

  .cart-item-quantity {
    width: percentage(1 / map-get($cart-item-cell, columns));

    .form-field-quantity {
      display: inline-block;
    }
  }

  .cart-item-total {
    display: none;
  }

  .cart-item-action {
    width: percentage(map-get($cart-item-cell, action) / map-get($cart-item-cell, columns));
  }
}

/* scss-combine:components/cart/content-medium */
//-- Cart Content -- Medium Up --//
@include breakpoint(m) {

  .cart-item-quantity {
    width: percentage(map-get($cart-item-cell, quantity) / map-get($cart-item-cell, columns));
  }

  .cart-item-total {
    display: table-cell;
    width: percentage(map-get($cart-item-cell, total) / map-get($cart-item-cell, columns));
  }
}

/* scss-combine:components/cart/content-large */
//-- Cart Content -- Large Up --//
@include breakpoint(l) {
  .cart-item-row .button {
    padding: $gutter-half $gutter-third;
    font-size: 1em;
  }
}

/* scss-combine:components/cart/content-giftwrap */
//-- Stylings for Giftwrap Cell --//
.giftwrap-item {
  margin-top: em(6px, $body-font-size);
}

.giftwrap-item-title {
  @include smallcaps;
  display: block;
  line-height: 1.4;
  color: $heading-color;
}

.giftwrap-item-actions {
  display: block;
  margin-top: $gutter-half;
}

.giftwrap-item-message {
  display: block;
  margin-top: $gutter-half;
  color: $body-color-alt;
}

.giftwrap-item-action {
  @extend %meta-text-treatment;
  padding-right: $gutter-half;
  font-size: em(12px);

  ~ .giftwrap-item-action {
    margin-left: $gutter-third;
  }

  svg {
    width: em(14px);
    height: em(14px);
    margin-right: $gutter-half / 2;
    fill: currentColor;
  }
}

.giftwrap-item-add {
  @include smallcaps;
  @include anchor-underline("off-hover", $body-color-alt, $body-color);
  position: relative;
  display: inline-block;
  padding-left: $gutter-half;
  font-size: em(10px, $body-font-size);
  font-weight: get-font-weight($weight: "bold");

  @include breakpoint(ms-only) {
    &:before {
      display: inline-block;
      content: attr(data-text-short);
    }
  }

  &:before {
    display: inline-block;
    content: attr(data-text-long);
  }

  svg {
    position: absolute;
    top: 50%;
    left: 0;
    width: 9px;
    height: 9px;
    margin-top: -5px;
    fill: currentColor;
  }
}

/* scss-combine:components/cart/totals */
//-- Cart Totals --//
.cart-totals-row {
  //scss-lint:disable PropertySortOrder
  @include container();
  display: flex;
  flex: 1;
  flex-direction: row;
  flex-flow: row wrap;
  border-bottom: 1px solid $content-border-color;

  .cart-total-cell {
    width: 100%;
    padding: $gutter 0;
    border-top: 1px solid $content-border-color;

    @include breakpoint(m) {
      width: 50%;
      border-top: 0;

      &:first-child {
        @include first();
        border-left: 0;
      }
    }

    &:first-child {
      border-top: 0;
    }
  }

  .form-field {
    position: relative;

    .form-label-text {
      @extend %show-for-sr;
    }

    .form-icon-button {
      position: absolute;
      top: 0;
      right: 0;
      bottom: 0;
    }
  }

  .alert {
    padding: $gutter-third $gutter-half;
    margin-top: $gutter-half;
    font-size: $h6-font-base;

    &.alert-error {
      @extend %meta-text-treatment;
      padding: 0;
      font-size: 0.8em;
      color: $error-color;
      background-color: transparent;
      border-color: transparent;
    }
  }
}

.cart-totals-heading {
  @extend %meta-text-treatment;
  margin-top: 0;
  margin-bottom: 0;
  font-size: em(12px, $body-font-size);
  letter-spacing: em(2px, $body-font-size);

  .cart-totals-toggle {
    @extend %primary-link-style;
    float: right;
    font-size: 1em;
    border-bottom: 1px solid $heading-color;

    &:hover {
      cursor: pointer;
      border-bottom-color: transparent;
    }

    &.active svg use {
      transform: rotate(45deg);
    }

    svg {
      width: em(12px);
      height: em(12px);
      fill: currentColor;
    }

    use {
      transform: rotate(0deg);
      transform-origin: 50% 50%;
    }

  }
}

//-- Cart Totals Forms --//
[data-coupon-code-form],
[data-gift-certificate-form] {
  display: none;

  &.visible {
    display: block;
  }
}

//--Totals - Cart Codes --//
.cart-total-codes {
  > [class*="cart-total-"] {
    padding: $gutter 0;
    border-top: 1px solid $border-color;

    @include breakpoint(m) {
      margin-right: $gutter;
    }

    &:first-child {
      padding-top: 0;
      border: 0;
    }

    &:last-child {
      padding-bottom: 0;
    }
  }

  form {
    margin-top: $gutter-half;
  }
}

//-- Totals - Coupon Cell --//
.coupon-item-toggle-wrapper {
  margin: $gutter-third 0;
}

.coupon-item-applied {
  padding: $gutter-half;
  margin: $gutter 0;
  background-color: contrast($content-background, 3%);
}

//-- Totals - Gift Certificate Cell --//
.gift-certificate-applied {
  padding: $gutter-half;
  margin: $gutter 0;
  background-color: contrast($content-background, 3%);

  span {
    display: block;
  }

  + form {
    padding-top: $gutter-third;
  }
}

//-- Totals - Overview / Pricing Information --//
.cart-total-overview {
  @include breakpoint(m) {
    padding-left: $gutter;
  }
}

.cart-total-item {
  @include container();
  @include smallcaps;
  padding-top: $gutter-half;
  font-size: em(12px, $body-font-size);
  color: $meta-color;
}

.cart-total-item-label {
  @include span(5 of 12 first);
}

.cart-total-item-value {
  @include span(7 of 12);
  line-height: 1;
  color: $meta-color;
  text-align: right;

  .cart-total & {
    color: $cart-total-value-color;
  }
}

.cart-total-link {
  @extend %primary-font, %primary-link-style;
  font-size: 1em;
  border-bottom: 1px solid $heading-color;

  &:hover {
    border-bottom-color: transparent;
  }

  svg {
    width: em(12px);
    height: em(12px);
    fill: currentColor;
  }
}

//-- Totals - Actions --//
.cart-actions {
  padding-top: $gutter;
  padding-bottom: $gutter;
  text-align: center;

  @include breakpoint(ms) { text-align: right; }

  .continue-shopping {
    @extend %primary-link-style;
    display: block;
    width: 100%;
    margin-bottom: $gutter;
    text-align: center;
    border-bottom: 1px solid $heading-color;

    &:hover {
      border-bottom-color: transparent;
    }

    @include breakpoint(ms) {
      float: left;
      width: auto;
      margin-bottom: 0;
    }
  }
}

.cart-additional-checkout-buttons {
  @extend %clearfix;
  margin-top: $gutter-half;
  text-align: center;

  @include breakpoint(ms) { text-align: right; }

  // scss-lint:disable ImportantRule, QualifyingElement
  p {
    float: none !important;
    margin: em($gutter-half);
    color: $meta-color;
  }

  form { clear: both;}
}

/* scss-combine:components/cart/apple-pay */
.apple-pay-checkout-button {
  display: none;
  max-height: em(64px);
  min-width: em(200px);
  min-height: em(32px);
  padding: em(20px);
  margin-bottom: em($gutter-half);
  background: {
    position: 50% 50%;
    repeat: no-repeat;
    size: 100% 60%;
  }
  border-radius: 5px;
  box-sizing: border-box;

  //scss-lint:disable ColorKeyword ColorVariable
  @if $apple-pay-style == "white" {
    background-color: white;
    background-image: -webkit-named-image(apple-pay-logo-black);
  } @else if $apple-pay-style == "white-border" {
    background-color: white;
    background-image: -webkit-named-image(apple-pay-logo-black);
    border: 0.5px solid black;
  } @else {
    background-color: black;
    background-image: -webkit-named-image(apple-pay-logo-white);
  }

  .apple-pay-supported & { display: inline-block; }
}

/* scss-combine:components/collections/compare */
//-- Compare Bar Wrapper --//
.compare-bar {
  position: fixed;
  right: 0;
  bottom: -100%;
  left: 0;
  z-index: map-get($zindex, compare-bar);
  background-color: $content-background;
  transition: all 0.2s $ease-out-quad;

  @include breakpoint(s-down) {
    position: absolute;
    display: none;
  }

  &.visible {
    bottom: 0;
  }
}

.compare-bar-header,
.compare-bar-content {
  padding-right: $gutter;
  padding-left: $gutter;
}

//-- Compare Bar Header --//
.compare-bar-header {
  display: flex;
  align-content: flex-start;
  align-items: flex-start;
  flex-flow: column wrap;
  justify-content: flex-start;
  padding-top: $gutter-third;
  padding-bottom: $gutter-third;
  margin-bottom: -1px;
  text-align: left;
  vertical-align: middle;
  border-top: 1px solid $compare-bar-border;
  border-bottom: 1px solid $compare-bar-border;

  .layout-container {
    padding: $gutter-half $gutter-double;
    box-sizing: border-box;
  }

  .is-opened & {
    margin-bottom: 0;
  }

  @include breakpoint(m) {
    align-content: center;
    align-items: center;
    flex-flow: row wrap;
    justify-content: center;
  }
}

.compare-bar-title {
  @extend %as-h4;
  display: inline-block;
  flex: 1;
  height: 100%;
  margin: 0;
}

.compare-bar-buttons {
  flex: 1;
  margin-top: $gutter-half;
  text-align: left;

  @include breakpoint(m) {
    text-align: right;
  }
}

.compare-bar-toggle,
.compare-bar-submit {
  display: inline-block;

  @include breakpoint(l) {
    //same as @extend %button-large. But you can't extend in breakpoints
    padding: ($button-padding-vertical * 2) $button-padding-horizontal;
  }
}

.compare-bar-submit {
  opacity: 0;
  transition: opacity 0.2s $ease-out-quad;

  &.visible {
    opacity: 1;
  }
}

//-- Compare Bar Contents --//
.compare-bar-content {
  display: block;
  width: 100%;
  max-height: 0;
  overflow: hidden;
  font-size: 0;
  text-align: left;
  transition: all 0.5s linear;

  .is-opened & {
    max-height: 500px;
  }

  @include breakpoint(m){
    text-align: center;
  }
}

//-- Compare Bar Items --//
.compare-bar-item {
  position: relative;
  display: inline-block;
  width: 100px;
  margin: $gutter-half 0 $gutter-half $gutter-half;
  font-size: $body-font-size;

  &:first-child { margin-left: 0; }

  @include breakpoint(l) {
    width: 150px;
  }
}

.compare-item-border {
  border: 1px solid $compare-bar-border;
}

.compare-bar-image {
  @include aspect-ratio(map-get($product-item-image, width) map-get($product-item-image, height));
  background: {
    position: 50% 50%;
    repeat: no-repeat;
    size: cover;
  }
}

.compare-bar-item-add {
  @include aspect-ratio(map-get($product-item-image, width) map-get($product-item-image, height));
}

.compare-bar-item-text {
  @extend %font-smoothing;
  @include smallcaps();
  position: absolute;
  top: 50%;
  left: 50%;
  display: inline-block;
  padding: 0;
  margin: 0;
  font-size: em(12px, $body-font-size);
  line-height: 1.2;
  transform: translateY(-50%) translateX(-50%);
}

.compare-bar-item-remove {
  position: absolute;
  top: 0;
  right: 0;
  padding: 6px;
  background-color: $content-background;

  svg {
    display: block;
    width: 8px;
    height: 8px;
  }

  use {
    width: 100%;
    height: 100%;
    transition: fill 0.5s $ease-out-quad;
    fill: brightness-adjust($color: $compare-bar-border, $amount: 20%);
  }

  &:hover use {
    fill: $compare-bar-accent;
  }
}

/* scss-combine:components/header */

.main-header {
  position: relative;
  z-index: map-get($zindex, header);
  display: table;
  width: 100%;
  background-color: $header-background;

  &,
  a:not(.button) {
    color: $header-color;
    vertical-align: middle;
  }

  .header-menu {
    display: table;
    width: 100%;
    padding: 0 $gutter-half;
    border-top: 1px solid $header-background;
    border-bottom: 1px solid $header-background;
    box-sizing: border-box;
    table-layout: fixed;

    @include breakpoint(l) {
      padding-right: $gutter;
      padding-left: $gutter;
    }

    @if $header-enable-border {
      border-bottom-color: blend-multiply($content-border-color, $header-background);
    }

    &.has-banner {
      border-top-color: $content-border-color;

      //scss-lint:disable NestingDepth
      .mobile-nav-open & {
        border-top-color: $header-background;
      }
    }
  }
}

// -- Header Quick Nav && Header Tools && Header Logo -- //
.header-logo-wrapper,
.header-tools-wrapper,
.header-mobile-nav-wrapper,
.header-nav-wrapper {
  @extend %font-smoothing;
  display: table-cell;
  width: percentage(1 / 3);
  font-size: $header-font-size;
  font-weight: get-font-weight($weight: "bold");
  vertical-align: middle;
}

// -- Header Logo -- //
.header-logo-wrapper {
  height: $header-height;
  padding-top: 5px;
  padding-bottom: 5px;
  text-align: center;

  @include breakpoint(m) {
    text-align: left;
  }

  @include breakpoint(l) {
    height: $header-height-large;
  }

  .header-logo,
  .header-logo-text {
    padding: 0;
    margin: 0;
    font-size: $header-logotype-size;
  }

  .header-logo-text {
    line-height: 1;
  }

  .header-logo-image {
    max-width: 100%;
    max-height: $header-logo-size;

    @include breakpoint(ms) {
      max-width: 250px;
    }
  }

  a {
    display: inline-block;
    color: $header-color;
    border-bottom: 0;
  }

  img {
    display: block;
  }
}

//-- Mobile Menu Trigger --//
.header-mobile-nav-wrapper {
  @include breakpoint(m) {
    display: none;
  }
}

//-- Center navigation --//
.header-nav-wrapper {
  @include breakpoint(ms-down) {
    display: none;
  }

  .header-nav {
    display: block;
    margin: 0;
    text-align: center;
  }
}

.header-nav-item {
  position: relative;
  display: inline-block;
  margin-left: $gutter;

  &:first-child {
    margin-left: 0;
  }

  &.dropdown-active a,
  > a:hover {

    use {
      fill: $header-color-accent;
    }

    .header-nav-label:before {
      width: 100%;
    }
  }
}

.header-tools-item {
  display: none;

  @include breakpoint(m) {
    display: inline-block;
  }

  &.header-tools-item-cart {
    display: inline-block;
    vertical-align: bottom;
  }
}

.header-nav-item {
  @if $header-use-megamenu {
    position: inherit;

    &.header-nav-item-pages {
      position: relative;
    }
  }
}

//-- Tools / Far right navigation --//
.header-tools {
  text-align: right;
}

.header-nav-label {
  position: relative;
  display: inline-block;
  letter-spacing: em(1px, 12px);
  text-transform: uppercase;
  vertical-align: middle;

  &:before {
    position: absolute;
    top: calc(50% + 1em);
    right: 0;
    left: 50%;
    display: block;
    width: 0;
    height: 1px;
    background: mix($header-color, $header-background, 50%);
    content: "";
    transform: translateX(-50%);
    transition: all 0.2s $ease-out-quad;
  }
}

//-- Dropdown Arrows --//
.header-nav-arrow {
  position: relative;
  width: 4px;
  margin-top: 1px;
  margin-left: 4px;
  vertical-align: middle;

  &:after {
    display: inline-block;
    margin-bottom: em(3);
    border-top: 4px solid $header-color;
    border-right: 4px solid transparent;
    border-left: 4px solid transparent;
    content: "";
  }
}

//-- Header Menu Icons --//
.header-tools-search,
.header-tools-cart,
.header-tools-menu {
  position: relative;
  padding-left: 25px;
}

.header-tools-cart {
  display: block;

  @include breakpoint(m) {
    padding-left: 0;

    .header-tools-icon {
      display: none;
    }
  }

  .header-nav-arrow,
  .header-cart-label {
    @include breakpoint(ms-down) {
      display: none;
    }
  }
}

.header-tools-icon {
  position: absolute;
  top: 50%;
  left: 0;
  width: 18px;
  height: 18px;
  margin-top: -9px;

  use {
    transition: fill 0.4s $ease-out-quad;
    fill: $header-color;

    .dropdown-active & {
      fill: $header-color-accent;
    }
  }
}

/* scss-combine:components/header/search-modal */
//-- Modal Wrapper --//
.modal-header-search {

  .modal-content {
    position: initial;
    background-color: transparent;
  }
}

//-- Modal Form --//
.modal-search {
  position: relative;
  width: 100%;
  padding: $gutter-half 0;

  .form-field {
    width: 100%;
    vertical-align: middle;
  }

  .form-label-text,
  .form-field-title {
    @extend %show-for-sr;
  }

  .form-input {
    @extend %as-h3, %secondary-font;
    padding: $gutter-half (map-get($search-modal-icon, width) + $gutter-third + 18px) $gutter-half 0;
    color: $search-modal-color;
    background-color: transparent;
    border: 0;
    border-bottom: 1px solid $search-modal-color-accent;

    &::placeholder {
      color: $search-modal-color;
    }
  }

  .form-field-invalid {
    .form-input {
      background-image: none;
    }

    .form-inline-message {
      position: absolute;
      bottom: - $gutter;
      left: map-get($search-modal-icon, width) + $gutter-third;
      color: $search-modal-error-color;
    }
  }

  .form-field-valid .form-input {
    background-image: none;
  }
}

.modal-search-submit {
  position: absolute;
  top: 50%;
  right: 0;
  display: inline-block;
  width: map-get($search-modal-icon, width);
  height: map-get($search-modal-icon, height);
  margin-top: - map-get($search-modal-icon, width) / 2;
  vertical-align: middle;
  background: transparent;
  border: 0;
}

.modal-search-submit-icon {
  display: inline-block;
  max-width: 100%;
  max-height: 100%;
  vertical-align: middle;

  use {
    transition: fill 0.4s $ease-out-quad;
    fill: $search-modal-color;
  }

  &:hover use {
    fill: $search-modal-color
  }
}

/* scss-combine:components/header/dropdown */
//-- Header Dropdowns --//
.header-dropdown {
  @extend %header-box-shadow;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 100;
  line-height: 1.666;
  background-color: $header-background;
  opacity: 0;
  transition: opacity 0.3s $ease-out-quad, top 0.3s $ease-out-quad;

  &:not(.visible) {
    display: none;
  }

  &.animating-in,
  &.animating-out,
  &.animating {
    display: block;
  }

  &.visible {
    top: calc(100% + #{$header-dropdown-offset});
    display: block;
    opacity: 1;
    transition: all 0.3s $ease-out-quad;

    &.dropdown-currency,
    &.dropdown-quick-cart,
    &.dropdown-menu-style-mega-menu { top: 100%; }
  }

  .dropdown-column {
    height: 100%;
  }
}

//-- Common Dropdown li styles --//
.dropdown-link-list-item,
.dropdown-column-header {

  a {
    @include anchor-underline('on-hover', $color: $header-color, $hover: $header-color);
    text-transform: uppercase;
    transition: all 0.4s $ease-out-quad;
  }
}

.dropdown-column-header {
  padding-bottom: $header-dropdown-padding-small;
  line-height: 1.3;

  &:last-child {
    padding-bottom: 0;
  }
}

.dropdown-link-list-item {
  position: relative;
  display: block;
  padding-top: $header-dropdown-padding-small;
  padding-bottom: $header-dropdown-padding-small;
  line-height: 1.2;
}

.dropdown-menu {
  @extend %clearfix;
  left: 50%;
  text-align: left;
  transform: translateX(-50%);

  &.dropdown-menu-style-drill-down {
    min-width: $header-dropdown-shop;
    background: transparent;
    box-shadow: none;
  }

  &.dropdown-menu-style-mega-menu {
    align-content: flex-start;
    flex-flow: row;
    flex-wrap: wrap;
    justify-content: flex-start;
    width: (($max-width / 5) * $header-mega-menu-columns) - $gutter-double;
    max-width: calc(100% - #{$header-dropdown-padding-small * 2});
    padding: $header-dropdown-padding-small 0;

    &.animating,
    &.visible {
      display: flex;
    }
  }
}

//-- Applies to drill down --//
.header-dropdown-menu {
  @extend %header-box-shadow;
  padding: $header-dropdown-padding-small 0;
  background-color: $header-background;

  &.submenu {
    position: absolute;
    top: 50%;
    left: calc(100% - #{$gutter-third});
    min-width: $header-dropdown-shop;
  }

  a {
    font-weight: get-font-weight($weight: "medium");
    text-transform: uppercase;
  }

  .dropdown-link-list-item {
    padding: 0 $header-dropdown-padding-small;
    margin: $header-dropdown-padding-small 0;

    &.submenu-item {
      margin: em($header-dropdown-padding-small) 0;
    }

    &.has-children {
      padding-right: 2.25em;
    }

    &:first-child {
      margin-top: 0;
    }

    &:last-child {
      margin-bottom: 0;
    }
  }
}

//-- Applies to mega menu --//
.dropdown-menu-column {
  flex: 0 0 auto;
  width: calc(#{percentage(1 / $header-mega-menu-columns)} - 1px);
  padding: $header-dropdown-padding-small;
  border-right: 1px solid $content-border-color;

  &:nth-child(#{$header-mega-menu-columns}n) {
    border-right: 0;
  }

  &:nth-child(-n+#{$header-mega-menu-columns}) {
    margin-top: 0;
  }

  &:nth-child(#{$header-mega-menu-columns}n + 1) {
    clear: left;
  }

  .mega-menu-item a:hover {
    color: $header-color-accent;
  }
}

/* scss-combine:components/header/quick-cart */
//-- Quick Cart --//
.dropdown-quick-cart {
  right: -$gutter-half;
  left: auto;
  width: $header-dropdown-quick-cart-small;
  max-height: calc(100vh - #{$header-height} - #{$gutter-half});
  overflow-x: hidden;
  overflow-y: auto;
  text-align: left;

  @include breakpoint(m) {
    right: 0;
    width: $header-dropdown-quick-cart-large;
    max-height: calc(100vh - #{$header-height-large} - #{$gutter});
  }
}

.dropdown-column-quick-cart {
  right: 0;
  z-index: 10;
  display: block;
  font-size: $body-font-size;
}

.quick-cart-empty {
  padding: $header-dropdown-padding-small / 2;

  @include breakpoint(xl) {
    padding: $header-dropdown-padding-large / 2;
  }
}

.quick-cart-empty {
  @extend %meta-text-treatment;
  color: $header-color;
}

.quick-cart-item {
  @include layout-group($header-dropdown-padding-small);
}

.quick-cart-item-image-wrapper,
.quick-cart-item-details,
.quick-cart-item-remove-wrapper {
  @include layout-group-item(top)
}

.quick-cart-item-image-wrapper {
  width: 80px;
}

.quick-cart-item-image {
  display: block;
  width: 80px;
  height: 80px;
  background: {
    position: 50% 50%;
    repeat: no-repeat;
    size: cover;
  }

  img {
    @extend %show-for-sr;
  }
}

.quick-cart-item-remove-wrapper {
  width: 18px;
  height: 20px;
  text-align: center;
}

.quick-cart-item-details {
  font-size: em(13px, $body-font-size);
}

.quick-cart-item-title {
  @extend %primary-font, %primary-font-bold;
  margin: 0;
  font-size: 1em;
  line-height: 1.2;
  text-transform: uppercase;

  a {
    @include anchor-underline('none', $header-color, $header-color-accent)
  }
}

.quick-cart-item-quantity {
  @extend %primary-font-normal;
  display: inline-block;
  margin-left: 5px;
  font-size: em(10px, 13px);
}

.quick-cart-item-brand {
  @extend %meta-text-treatment;
  display: block;
  margin-top: 4px;
  margin-bottom: 2px;
  font-size: em(10px, 13px);
  line-height: 1.3;
}

.quick-cart-item-price {
  @extend %primary-font-bold;
  display: block;
  font-size: em(13px, $body-font-size);
}

.quick-cart-item-remove {
  display: block;
  padding: 0 5px;

  svg {
    vertical-align: top;

    &,
    use {
      width: 10px;
      height: 10px;
      transition: fill 0.4s $ease-out-quad;
      fill: $meta-color;
    }
  }

  &:hover use {
    fill: $heading-color;
  }
}

//-- Cart totals --//
.quick-cart-totals {
  padding: 0 $header-dropdown-padding-small;

  .cart-total-item {
    padding-top: 0;

    &:first-child {
      padding-top: $header-dropdown-padding-large / 2;
      border-top: 1px solid $content-border-color;
    }
  }

  .cart-total-item-label {
    text-align: left;
  }

  .cart-total-item-label,
  .cart-total-item-value {
    @extend %primary-font, %primary-font-normal;
    font-size: em(14px, $body-font-size);
  }
}

//-- Cart / Checkout buttons --//
.quick-cart-footer {
  @include layout-group($header-dropdown-padding-small);
  border-bottom: 1px solid $content-border-color;

  @include breakpoint(xl) {
    border-spacing: $header-dropdown-padding-small $header-dropdown-padding-small;
  }

  .quick-cart-footer-cell {
    display: table-cell;
  }
}

/* scss-combine:components/header/currency */

.dropdown-currency {
  right: 0;
  left: auto;
  min-width: $header-dropdown-currency;
  padding: $header-dropdown-padding-small;
  text-align: left;

  .dropdown-link-list-item {
    padding-top: 5px;
    padding-bottom: 5px;

    &:last-child {
      padding-bottom: 0;
    }

    &:first-child {
      padding-top: 0;
    }
  }
}

/* scss-combine:components/header/mobile-nav */
//-- Outer Wrapper --//
.navigation-wrap {
  position: absolute;
  top: 0;
  z-index: map-get($zindex, navigation-wrap);
  width: $mobile-menu-width-xs;
  height: 100vh;
  padding: 0;
  overflow-y: hidden;
  border-right: 1px solid $mobile-menu-border;
  opacity: 0;
  transition: all 0.4s $ease-out-quad;

  @include breakpoint(s) {
    width: $mobile-menu-width-s;
  }

  @include breakpoint(l-up) {
    background: transparent;
    opacity: 0;
  }

  .mobile-nav-open & { opacity: 1; }

  .mobile-nav-closed & {
    z-index: -2;
    background: transparent;
  }
}

//-- Menu Offsets --//
.mobile-nav-open {
  .main-header,
  .main-content,
  .main-footer {
    transform: translateX($mobile-menu-width-xs);

    @include breakpoint(s) {
      transform: translateX($mobile-menu-width-s);
    }
  }
}

//-- Inner menu --//
.mobile-nav-wrap {
  position: relative;
  top: 0;
  left: 0;
  z-index: -1;
  width: $mobile-menu-width-xs;
  height: 100vh;
  padding: 0;
  margin: 0;
  overflow: hidden;
  background-color: $header-background;
  transform: translateX(-$mobile-menu-width-xs);
  transition: all 0.2s $ease-out-quad;

  @include breakpoint(s) {
    width: $mobile-menu-width-s;
    transform: translateX(-$mobile-menu-width-s);
  }

  .mobile-nav-open & {
    z-index: 2;
    overflow-y: auto;
    transform: translateX(0);
  }
}

//------ Mobile Nav Header ------//
//-- Search Form --//
.mobile-search {
  position: relative;
  padding: $mobile-menu-padding;

  .mobile-search-submit {
    position: absolute;
    top: $mobile-menu-padding;
    bottom: $mobile-menu-padding;
    left: $mobile-menu-padding;
    padding: $gutter-third;
    background: transparent;
    border: 0;
  }

  .search-submit-icon {

    &,
    use {
      width: $mobile-menu-search-icon;
      height: $mobile-menu-search-icon;
    }

    use {
      transition: fill 0.5s $ease-out-quad;
      fill: $input-color;
    }

    &:hover,
    &:active,
    &:focus { fill: $header-color; }

  }

  .form-inline-message,
  .form-field-title {
    @extend %show-for-sr;
  }

  .form-input {
    padding-right: $mobile-menu-search-icon + $gutter-third;
    padding-left: $mobile-menu-search-icon + $gutter-third;
    font-size: em(12px, $body-font-size);
    line-height: 1;
    text-align: center;
  }
}

//-- Header Buttons --//
.mobile-header-buttons {
  width: 100%;
  padding: $mobile-menu-padding;
  font-size: 0;
  border-top: 1px solid $mobile-menu-border;

  .button {
    @extend %button-small;
    position: relative;
    z-index: 1;
    display: inline-block;
    width: 50%;
    font-size: 10px;
    color: $mobile-menu-buttons;
    background-color: $header-background;
    border-color: $mobile-menu-buttons;
    border-width: 2px;

    &:hover {
      z-index: 10;
      background: $mobile-menu-color-light;
    }

    &:last-child {
      margin-left: -1px;
    }
  }
}

//scss-lint:disable SelectorDepth
.mobile-nav {
  display: block;
  width: 100%;
  font-size: em(12px, $body-font-size);
  border-bottom: 1px solid $mobile-menu-border;

  .button-open-submenu {
    top: 0;
    right: 0;
    bottom: 0;
    width: auto;
    height: 100%;
    padding: 0 15px;
  }

  .nav-menu-item {
    a {
      display: block;
      padding: $mobile-menu-padding;
      font-size: 1em;
      line-height: 1.4;
      color: $header-color;
      text-transform: uppercase;
      border-top: 1px solid $mobile-menu-border;
    }

    &.has-children > a {
      padding-right: $mobile-menu-dropdown-icon * 2.5;
    }
  }

  .submenu a {
    background-color: $mobile-menu-color-light;
    transition: all 0.5s $ease-out-quad;

    &:active,
    &:hover {
      background-color: contrast($mobile-menu-color-light, 5%);
    }
  }

  .submenu .submenu a {
    background-color: $mobile-menu-color-medium;
    border-top-color: contrast($mobile-menu-color-medium, 10%);

    &:active,
    &:hover {
      background-color: contrast($mobile-menu-color-medium, 5%);
    }
  }

  .submenu .submenu .submenu a {
    background-color: $mobile-menu-color-dark;
    border-top-color: contrast($mobile-menu-color-dark, 10%);

    &:active,
    &:hover {
      background-color: contrast($mobile-menu-color-dark, 5%);
    }
  }
}


/* scss-combine:components/header/submenu-button */
//-- Menu items with children --//
.nav-menu-item {
  position: relative;
}

//-- Submenu Button--//
.button-open-submenu {
  position: absolute;
  top: 0.3em;
  right: 1.25em;
  width: 0.8em;
  height: 0.8em;
  padding: 0;
  color: inherit;
  background: transparent;
  border: 0;
  opacity: 1;

  &:hover {
    opacity: 1;

    use {
      fill: $header-color-accent;
    }
  }

  &:focus { outline: 0; }

  &.active {

    .icon-increment-vertical {
      transform: scale(1, 0);
    }

  }

  svg {
    display: block;
    width: 0.8em;
    height: 0.8em;
    margin-top: 0;
    transition: fill 0.2s $ease-out-quad;
    fill: $header-color;
  }
}

//-- Submenu--//
.submenu {
  display: none;
  transition: height 0.2s ease 0s, opacity 0.2s ease 0.1s;

  &.animating,
  &.visible {
    display: block;
  }

  // Initial State
  &.animating {
    height: 0;
    opacity: 0;
  }

  &.animating-out {
    transition: height 0.2s ease 0s, opacity 0.1s ease 0s;
  }

  // Final State
  &.visible {
    opacity: 1;
  }
}

.submenu-item {

  a {
    display: inline-block;
    font-size: 0.9em;
    line-height: 1.2em;
  }
}

/* scss-combine:components/footer */
//------ Modal Wrappers ------//
.footer-modals-wrapper {
  display: none;
  visibility: hidden;
}

.footer-container {
  font-size: em($footer-font-base, $body-font-size);
  background-color: $footer-background;

  @if ($footer-background == $newsletter-background) {
    border-top: 1px solid $footer-border-color;
  }

  a {
    color: $footer-primary;
    transition: all 0.2s $ease-out-quad;

    &:hover {
      color: $footer-secondary;
    }
  }
}

.footer-navigation {
  @extend %clearfix;
  position: relative;
  padding-top: $gutter-double;
  padding-bottom: $gutter-double;

  @include breakpoint(ms) {
    padding-bottom: $gutter-half;
  }
}

//-- Styles for the footer payment icons / attribute area --//
.footer-interaction {
  @extend %clearfix;
  padding-top: $gutter-half;
  padding-bottom: $gutter-double;
  margin-top: $gutter-third;

  @if lightness($footer-background) > 50% {
    border-top: 1px solid $content-border-color;
  } @else {
    border-top: 1px solid contrast($footer-background, 25%);
  }

  @include breakpoint(m) {
    margin-top: $gutter-double;
  }
}

.footer-attribution {
  @extend %tertiary-font;
  color: $footer-secondary;

  @include breakpoint(s) {
    @include span(6 of 12 first);
  }

  a {
    color: $footer-tertiary;
  }

  .footer-credits {
    display: block;
  }
}

.footer-payment-icons {
  @include breakpoint(m) {
    @include span(6 of 12);
    text-align: right;
  }
}

//-- Return to the top ofthe page --//
.footer-back-to-top {
  position: absolute;
  top: -30px;
  right: 0;
  display: inline-block;
  width: em(60px, 14px);
  height: em(60px, 14px);
  background: $primary-button-background;
  border-radius: 100%;
  transform: rotate(-90deg);
  transition: fill 0.5s $ease-out-quad;

  svg {
    @extend %subpixel-smooth;
    position: absolute;
    left: 50%;
    display: block;
    max-width: 25px;
    max-height: 100%;
    transform: translateX(-50%);
    fill: $primary-button-color;
  }

  &:hover {
    background: $primary-button-accent-background;
  }
}

/* scss-combine:components/footer/footer-recent-posts */
.footer-blog-recent-posts {
  @extend %clearfix;
  padding-top: $gutter;
  padding-bottom: $gutter;
  border-top: 1px solid $content-border-color;

  @include breakpoint(m) {
    padding-top: $gutter-double;
    padding-bottom: $gutter-double;
  }

  @if($header-enable-border){
    &:first-child {
      border-top: 0;
    }
  }
}

.footer-recent-posts-header {
  padding-bottom: $gutter;
  text-align: center;

  @include breakpoint(m) {
    padding-bottom: $gutter-double;
  }
}

/* scss-combine:components/footer/footer-navigation */
//-- Styles for the Footer Navigation / Meta area --//
.footer-column {
  @include breakpoint(xs) {
    @include squish(1);
    padding-top: $gutter;

    &:first-child {
      padding-top: 0;
    }
  }

  @include breakpoint(s-only) {
    @include gallery(6 of 12);
    padding-top: $gutter;

    &:nth-child(1),
    &:nth-child(2) {
      padding-top: 0;
    }
  }

  @include breakpoint(ms) {
    @include span(4 of 12);
    padding-top: $gutter;

    &:nth-child(1),
    &:nth-child(2) {
      padding-top: 0;
    }

    &:first-child {
      @include span(8 of 12 first);
    }
  }

  @include breakpoint(ms-only) {

    &:nth-child(3) {
      @include first();
      clear: both;
    }
  }

  @include breakpoint(m) {
    @include span(2);
    padding-top: 0;

    &:first-child {
      @include span(4 first);
      @include first();

      .alternate-footer-layout & {
        @include span(6 first);
      }
    }
  }

  .footer-contact-address,
  .footer-contact-phone {
    @extend %tertiary-font;
  }
}

.footer-title {
  @extend %as-h6, %secondary-font, %secondary-font-normal;
  display: block;
  margin-bottom: $gutter / 2;
  color: $footer-secondary;
  text-transform: none;
}

.footer-contact-address {
  margin-bottom: $gutter / 2;
  font-style: normal;
  color: $footer-secondary;
}

.footer-contact-phone {
  color: $footer-secondary;
}

.footer-payment-icons,
.footer-social-icons {
  li {
    display: inline-block;
    margin-bottom: $gutter / 2;
    margin-left: $gutter / 2;
  }

  a {
    border-bottom: 0;
  }
}

.footer-social-icons {
  margin-left: -$gutter / 2;
}

.footer-payment-icon,
.footer-social-icon {
  width: em(30px);
  height: em(30px);

  use {
    transition: all 0.2s $ease-out-quad;
    fill: $footer-secondary;

    a:hover & {
      fill: $footer-primary;
    }
  }
}

.footer-link-list {

  li {
    margin-top: (em(30, $body-font-size) - 1em) / 2;
    line-height: 145%;

    &:first-child {
      margin-top: 0;
    }
  }

  a {
    @extend %primary-font-bold;
    @include smallcaps(1px);
    @include anchor-underline('on-hover', $footer-primary, $footer-primary);
    font-size: em(12px, $footer-font-base);
    line-height: 115%;

  }
}

/* scss-combine:components/footer/footer-attribution */

/* scss-combine:components/footer/footer-newsletter */
.footer-newsletter {
  padding-top: em(65px);
  padding-bottom: em(65px);
  text-align: left;
  background-color: $newsletter-background;
  border-top: 1px solid $content-border-color;

  @include breakpoint(m) {
    padding-top: em(80px);
    padding-bottom: em(80px);
  }

  .layout-container {
    width: $newsletter-width;
    max-width: 100%;
    margin: 0 auto;
  }

  @if($alternate-style-for-newsletter == false){
    .template-account &,
    .template-blog &,
    .template-brands &,
    .template-cart &,
    .template-contains &,
    .template-giftcertificates &,
    .template-page &,
    .template-newsletter_subscribe {
      border-top: 0;
    }
  }
}

.footer-newsletter-form {
  position: relative;

  @include breakpoint(s-down) {

    .form-action {
      margin-top: $gutter;
    }
  }

  @include breakpoint(ms) {
    @include layout-group();

    .form-field,
    .form-action {
      @include layout-group-item(middle);
    }

    .form-action {
      width: 160px;
      overflow: hidden;
    }

    .form-input,
    .button {
      height: 58px;
      max-height: 100%;
    }
  }

  .form-field-title {
    @extend %show-for-sr;
  }

  .form-input {
    padding: em($gutter-third) $gutter-half;
    font-size: em(18px);
    color: $newsletter-input-color;
    background-color: $newsletter-input-background;
    border-color: $newsletter-input-border;

    &::placeholder {
      @extend %secondary-font;
      @extend %secondary-font-normal;
    }
  }

  .form-inline-message {
    position: absolute;
    padding-top: $gutter-half;
  }
}

/* scss-combine:components/card-item */
//-- Card Grid --//

.cards-row {
  @extend %clearfix;

  + .cards-row {
    margin-top: gutter();
  }
}

.alternate-card-item,
.card-item {

  &:first-child {
    margin-top: 0;
  }

  .row-of-3 & {
    @include breakpoint(xs) {
      @include span(full);
      margin-top: gutter();
    }

    @include breakpoint(s) {
      @include gallery(6 of 12);
      margin-top: gutter();

      &:nth-child(-n+2) {
        margin-top: 0;
      }
    }

    @include breakpoint(ms) {
      @include gallery(4 of 12);
      margin-top: gutter();

      &:nth-child(-n+3) {
        margin-top: 0;
      }
    }
  }

  .row-of-2 & {
    @include breakpoint(xs) {
      @include span(full);
      margin-top: gutter();
    }

    @include breakpoint(s) {
      @include gallery(6 of 12);
      margin-top: gutter();

      &:nth-child(-n+2) {
        margin-top: 0;
      }
    }
  }
}

//-- Inside Card --//
.card-item-thumbnail {
  @include aspect-ratio(map-get($card-image, width) map-get($card-image, height));
  display: block;
  background: 50% 50% transparent no-repeat;
  background-size: cover;

  &.no-image {
    border-top: 1px solid $content-border-color;
    border-right: 1px solid $content-border-color;
    border-left: 1px solid $content-border-color;

    .alternate-card-item & {
      border-width: 0;
    }
  }

  img {
    @extend %show-for-sr;
  }
}

.card-item-details {
  padding: $gutter-half 0;
  text-align: center;
  border: {
    right: 1px solid $content-border-color;
    bottom: 1px solid $content-border-color;
    left: 1px solid $content-border-color;
  }

  .hide-image & {
    padding: $gutter $gutter-half;
    border-top: 1px solid $content-border-color;
  }

  .alternate-card-item & {
    text-align: left;
    border-width: 0;
  }
}

.card-item-title {
  @extend %as-h3;
  margin-bottom: 0;

  .hide-image & {
    margin-top: 0;
  }

  .small-headers & {
    font-size: $h3-font-size * 0.8;
  }
}

.card-item-content,
.card-item-title,
.card-item-details .button {
  margin-top: $gutter-half;
}

.card-item-content {
  @extend %tertiary-font;
}

//-- Alternate Card adjustments --//
.alternate-card-item {

  .card-item-link {
    @extend %meta-text-treatment;
    @include anchor-underline("on-hover", $heading-color, $body-color-alt);
    position: relative;
    display: inline-block;
    margin-top: $gutter-half;
    font-size: em(12px, $body-font-size);
    font-weight: get-font-weight($weight: "bold");
    transition: all 0.4s $ease-out-quad;

    &:hover svg {
      fill: $body-color-alt;
    }

    svg {
      position: absolute;
      top: 1px;
      right: em(-30px, $body-font-size);
      width: em(25px, $body-font-size);
      height: em(20px, $body-font-size);
      line-height: 1;
      transition: fill 0.4s $ease-out-quad;
    }
  }
}

/* scss-combine:components/breadcrumbs */

.page-breadcrumbs {
  @extend %collection-header-list;
  padding-top: $gutter;
  padding-bottom: $gutter;

  .breadcrumb-item { display: inline-block; }

  .breadcrumb-label {
    @extend %collection-header-list-item;
    letter-spacing: em(2px, 12px);
    opacity: 1;
    transition: all 0.4s $ease-out-quad;

    &.is-active {  border-color: transparent; }
  }

  .is-active { opacity: 0.5; }
}

/* scss-combine:components/pagination */

.pagination {
  @extend %meta-text-treatment;
  @include layout-group();
  width: 100%;
}

.pagination-item {
  @include layout-group-item(middle);
  width: percentage(1 / 2);
  table-layout: fixed;

  @include breakpoint(ms) {
    width: percentage(1 / 3);
  }

  a {
    @include smallcaps();
    display: inline-block;
    font-size: em(12, $body-font-size);
    color: $tab-color;
    transition: all 0.5s $ease-out-quad;

    &:hover {
      color: $tab-color-active;

      //scss-lint:disable NestingDepth
      use { fill: $tab-color-active; }
    }
  }
}

.pagination-count {
  display: none;
  text-align: center;

  @include breakpoint(ms) {
    display: table-cell;
  }
}

.pagination-next {
  text-align: right;
}

.pagination-links {
  text-align: center;
}

.pagination-link-item {
  display: inline-block;
  padding-right: $gutter-third / 2;
  padding-left: $gutter-third / 2;

  &:first-child {
    padding-left: 0;
  }

  &:last-child {
    padding-right: 0;
  }

  a {
    @extend %tab-link;
    padding-right: $gutter-third / 2;
    padding-left: $gutter-third / 2;

    &.active {
      @extend %tab-link-active;
    }
  }
}

.pagination-page {
  position: relative;
}

.pagination-icon {
  position: relative;
  top: -1px;
  display: inline-block;
  width: em(25px, $body-font-size);
  height: em(20px, $body-font-size);
  vertical-align: middle;

  .pagination-previous & {
    transform: rotate(-180deg);
  }

  use {
    transition: fill 0.5s $ease-out-quad;
    fill: $tab-color;
  }
}

/* scss-combine:components/product/images */
// Product Images
.product-slideshow {
  display: block;
  float: left;
  width: 100%;
  transform: translate3d(0, 0, 0);

  @include breakpoint(m-down) {
    text-align: center;
  }
}

.product-slideshow-images {
  position: relative;
  min-height: 400px;

  @include breakpoint(m-down) { min-height: 20px; }

  .loading-spinner {
    @include centerer();
    top: 300px;
    opacity: 1;
    visibility: visible;
    transition: opacity 0.3s $ease-out-quad;

    @include breakpoint(m-down) { display: none; }

    .quick-shop & { display: none; }
  }

  &.flickity-enabled {
    min-height: 0;

    > .product-single-sale-badge { display: block; }

    > .loading-spinner {
      opacity: 0;
      visibility: hidden;
    }
  }

  .product-single-sale-badge {
    @extend %subpixel-smooth, %meta-text-treatment;
    position: absolute;
    bottom: 0;
    left: 0;
    z-index: 10;
    display: none;
    padding: 7px 10px;
    font-size: em(13, 16);
    color: $product-item-sale-color;
    pointer-events: none;
    background-color: $product-item-sale-background;
  }

  .product-slideshow-image {
    display: none;
    width: 100%;
    height: auto;
    text-align: center;

    &:first-child { 
      display: block;
      visibility: hidden;
    }

    img {
      display: block;
      margin: 0 auto;
    }
  }

  .flickity-viewport {
    margin-bottom: 0;

    .product-slideshow-image {
      display: block;
      visibility: visible;
    }
  }

  .flickity-page-dots {
    position: absolute;
    bottom: $gutter-half;
    left: 50%;
    display: none;
    width: calc(100% - #{($zoom-icon-size + ($zoom-icon-padding * 3)) * 2});
    transform: translateX(-50%);
  }

  .flickity-prev-next-button {
    display: none;

    @include breakpoint(m) {
      width: $carousel-arrow-size / 1.3;
      height: $carousel-arrow-size / 1.3;
    }
  }

  .flickity-prev-next-button,
  .flickity-page-dots {
    opacity: 0;
    transition: opacity 0.4s $ease-out-quad;

    .visible-controls & { display: block; }
    .product-slideshow:hover & { opacity: 1; }
  }
}

.product-image-zoom {
  @extend %button-base, %button-secondary;
  position: absolute;
  right: 0;
  bottom: 0;
  display: inline-block;
  padding: $zoom-icon-padding;
  border-width: 0;
  opacity: 0;
  transition: all 0.4s $ease-out-quad;

  svg {
    display: block;
    width: $zoom-icon-size;
    height: $zoom-icon-size;
    transition: all 0.4s $ease-out-quad;
    fill: $secondary-button-color;
  }

  &:hover svg { fill: $secondary-button-accent-color; }

  .product-slideshow:hover & { opacity: 1; }
}

// Product Thumbnails
.product-thumbnails {
  @extend %clearfix;
  display: none;
  margin-right: - $gutter-half;
  margin-left: - $gutter-half;

  @include breakpoint(m-down) { font-size: 0; }

  .flickity-enabled + & { display: block; }
}

.product-thumbnail {
  @include aspect-ratio(1 1);
  display: inline-block;
  float: left;
  width: calc(25% - #{$gutter});
  padding-bottom: calc(25% - #{$gutter});
  margin-top: $gutter;
  margin-right: $gutter-half;
  margin-left: $gutter-half;
  cursor: pointer;
  background-color: transparent;
  background-position: 50% 50%;
  background-size: cover;
  transition: opacity 0.2s $ease-out-quad;

  @include breakpoint(m-down) {
    display: inline-block;
    float: none;
    margin: 10px 5px;
    vertical-align: top;
  }

  &:hover { opacity: 0.75; }
}

/* scss-combine:components/product/options */
//-- Set Items Only--//
%option-set-item {
  position: relative;
  display: block;
  float: left;
  margin-right: map-get($product-option, space);
  margin-bottom: map-get($product-option, space);
}

.form-field-customizations,
.form-field-options {
  padding: $product-gutter-half 0;

  &.form-field-swatch {
    padding-bottom: $product-gutter-half - map-get($product-swatch, space);
  }

  .option-unavailable {

    &,
    &:hover {
      opacity: 0.5;
    }

    .form-label-text { text-decoration: line-through; }
  }

  @include breakpoint(l) {
    display: block;
    max-width: $input-max-width;
    margin: 0 auto;

    .modal-quick-shop & {
      max-width: 100%;
    }
  }

  @if ($content-same-as-background == false) {
    border-bottom: 1px solid $content-border-color;
  }
}

//-- Date Fields --//
.form-field-date {

  .form-field-control {
    @extend %clearfix;
  }

  .form-select-wrapper {
    display: block;
    float: left;
    width: calc(33.33% - 6px);
    margin-right: 9px;

    &:last-child {
      margin-right: 0;
    }
  }
}

//-- Form Rectangle Options --//
.form-field-rectangle {
  .form-field-control {
    @extend %clearfix;
    font-size: 0;
  }

  .form-input {
    @extend %show-for-sr;
  }

  .rectangle {
    display: inline-block;
    margin-right: map-get($product-option, padding);
    font-size: $body-font-size;
  }

  .rectangle-text {
    position: relative;
    z-index: 1;
    display: block;
    height: 55px;
    min-width: 55px;
    padding: 0 map-get($product-option, padding);
    margin-bottom: map-get($product-option, padding);
    margin-left: -1px;
    overflow: hidden;
    line-height: 52px;
    text-align: center;
    text-overflow: ellipsis;
    white-space: nowrap;
    cursor: pointer;
    border: 2px solid $input-border-color;
    transition: all 0.4s $ease-out-quad;
  }

  .rectangle-text:hover {
    color: $body-color-alt;
    border-color: map-get($product-option, hover);
  }

  .form-input:focus + .rectangle-text {
    color: map-get($product-option, selected);
  }

  .form-input:checked + .rectangle-text {
    position: relative;
    z-index: 10;
    color: map-get($product-option, selected);
    border-color: map-get($product-option, selected);
  }
}

//-- Swatch Picker --//
.form-field-swatch {
  .form-field-control {
    @extend %clearfix;
    display: inline-block;
  }

  .form-input,
  .form-label-text {
    @extend %show-for-sr;
  }

  .swatch-wrap {
    position: relative;
    float: left;
    width: map-get($product-swatch, size);
    height: map-get($product-swatch, size);
    margin-right: map-get($product-swatch, space);
    margin-bottom: map-get($product-swatch, space);

    &:last-of-type {
      margin-right: 0;
    }

    &:hover .swatch-pattern-expanded {
      opacity: 1;
      visibility: visible;
    }
  }

  .swatch {
    position: relative;
    display: block;
    width: map-get($product-swatch, size);
    height: map-get($product-swatch, size);
    padding: 0;
    cursor: pointer;
    transition: all 0.4s $ease-out-quad;
    user-select: none;

    &:before,
    &:after {
      position: absolute;
      top: 0;
      left: 0;
      display: block;
      width: map-get($product-swatch, size);
      height: map-get($product-swatch, size);
      content: "";
    }

    &:before {
      z-index: 2;
      box-shadow: inset 0 0 0 1px $border-color;
    }

    &:after {
      z-index: 1;
      box-shadow: inset 0 0 0 3px $content-background;
    }

    &:hover:before { box-shadow: inset 0 0 0 1px map-get($product-option, hover); }
  }

  .form-input:checked + .swatch {
    &:before {
      box-shadow: inset 0 0 0 1px map-get($product-option, selected);
    }

    &:after {
      box-shadow: inset 0 0 0 4px $content-background;
    }
  }


  .swatch-color {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-size: cover;
  }

  .swatch-value {
    font-style: italic;
    color: $meta-color;
    text-transform: capitalize;

    @include breakpoint(m-only) {
      display: block;
      margin-top: 10px;
    }
  }

  .two-colors {
    .secondary {
      top: 50%;
    }
  }

  .three-colors {
    .secondary {
      top: 33.33%;
    }

    .tertiary {
      top: 66.66%;
    }
  }

  .swatch-pattern-expanded {
    position: absolute;
    top: calc(100% + #{map-get($product-swatch, border) * 2});
    left: 50%;
    z-index: 100;
    padding: map-get($product-swatch, border);
    background-color: $content-background;
    border: 1px solid map-get($product-option, hover);
    opacity: 0;
    visibility: hidden;
    transform: translate(-50%, 0);
    transition: opacity 0.4s $ease-out-quad;

    @include breakpoint(ms-down) { display: none; }
  }

  .swatch-pattern-image {
    display: block;
    width: 100px;
    height: auto;
    max-width: 100px;
  }
}

//-- File Upload --//
.form-file-types {
  display: block;
  margin-top: $gutter-third;
}

//-- Product Picklist --//
.form-field-product-list {
  width: 100%;

  .form-field-control {
    padding: $gutter-half;
    text-align: left;
    background-color: $content-background;
    border: 1px solid $border-color;

    .form-label {
      position: relative;
      padding-top: em(5px, $body-font-size);
      margin-top: em(5px, $body-font-size);
      border-top: 1px solid $border-color;
    }

    .form-label:first-child {
      padding-top: 0;
      margin-top: 0;
      border-top: 0;
    }
  }
}

.product-picklist-item,
.product-picklist-no-image {
  position: relative;
  padding: map-get($product-pick-list, padding);
  opacity: 1;
  transition: all 0.2s $ease-out-quad;

  &:last-child {
    &:after {
      display: none;
    }
  }

  .product-picklist-image-wrap {
    padding: map-get($product-pick-list, padding) / 3;
    margin-right: 10px;
    border: 1px solid $border-color;
    transition: all 0.2s $ease-out-quad;
  }

  img {
    display: block;
  }

  &:hover {
    cursor: pointer;
    opacity: 0.7;
  }
}

.form-input.product-picklist-radio,
.product-picklist-item-body,
.product-picklist-image-wrap,
.product-picklist-label-text {
  display: inline-block;
  vertical-align: middle;
}

.product-picklist-radio:checked {
  + .product-picklist-item-body {
    color: $accent-color;

    .product-picklist-image-wrap {
      border-color: $accent-color;
    }

    .form-label-text {
      color: $accent-color;

      //scss-lint:disable NestingDepth
      &:after {
        background-color: $accent-color;
      }
    }
  }
}

.product-picklist-item-body {
  width: 100%;

  &.picklist-none {
    font-style: normal;
    line-height: 1;

    .form-label-text {
      margin: map-get($product-pick-list, padding) 0;
    }
  }
}

.product-picklist-label-text {
  margin-bottom: 0;
  font-style: italic;
  text-transform: none;
}

.product-picklist-image-wrap {
  max-width: map-get($product-pick-list, size);
}

.product-picklist-image {
  display: block;
}


//-- Product Events --//
.product-event-bounds {
  @extend %meta-text-treatment;
  display: inline-block;
  margin-top: $gutter-third / 2;
  font-size: $h6-font-size;
}

/* scss-combine:components/product/price */
.product-price,
.product-item-price,
.price-saved,
.price-withouttax,
.price-with-tax {
  display: block;
  white-space: normal;
}

.price {
  @extend %secondary-font, %secondary-font-normal;
  color: $meta-color;
}

.product-price {
  margin: $gutter-third 0;
}

.price-rrp {
  color: $price-color-alternate;
  text-decoration: line-through;

  + .price-value {
    &:before {
      display: inline-block;
      content: " ";
    }
  }

}

.price-tax-label {
  padding-left: $gutter-third / 2;
}

/* scss-combine:components/product/tabs */
.module-product-tabs {
  background-color: $content-background;
}

.module-product-tabs-header {

  @include breakpoint(ms) {
    border-top: 1px solid $content-border-color;
    border-bottom: 1px solid $content-border-color;
  }
}

.product-tabs {
  font-size: 0;
  text-align: center;
  border-bottom: 0;

  @include breakpoint(s-down) {
    @include clearfix();
  }

  @include breakpoint(ms) {
    text-align: left;
  }
}

.product-tab-title {
  display: inline-block;
  font-size: $body-font-size;

  @include breakpoint(xs) {
    @include span(12 first);
  }
  @include breakpoint(s-only) {
    @include gallery(6)
  }

  @include breakpoint(ms) {
    width: auto;
    margin: 0 0 0 $product-gutter;

    &:first-child {
      margin-left: 0;
    }
  }

  .share-buttons-list-trigger,
  .tab-link {
    font-size: em(12px, $body-font-size);
    text-align: center;
    border-bottom: 2px solid $border-color;

    @include breakpoint(ms) {
      border-bottom: 2px solid transparent;
    }

    @include breakpoint(m-down) {
      padding: ($product-gutter-half / 2) 0;
    }
  }

  .share-buttons-list-trigger {
    &,
    &:hover {
      border-bottom: transparent;
    }
  }
}

.product-tabs-content {
  padding: $product-gutter-half 0;

  .product-reviews,
  .rte {
    @extend %tertiary-font;
  }

  .rte {
    text-align: center;

    @include breakpoint(ms) {
      text-align: left;
    }
  }
}

//-- Information Tab --//
.product-information-block {
  @extend %clearfix;
  display: block;
  vertical-align: middle;
}

.product-information-name,
.product-information-value {
  display: inline-block;
  float: left;
}

.product-information-name {
  clear: both;
}

.product-information-value {
  display: inline-block;
  margin-left: 5px;
}

//-- Video Tab --//
.product-video-container {
  margin-top: $gutter;

  &:first-child {
    margin-top: 0;
  }
}

/* scss-combine:components/product/reviews */
//-- Review Header --//
.review-header {
  vertical-align: middle;

  .review-count,
  .review-link,
  .rating-stars {
    display: inline-block;
    line-height: 1;
    vertical-align: middle;
  }

  .review-count {
    padding-left: $gutter-third / 2;
  }

  .rating-stars {
    padding-bottom: 3px;
  }

  .review-link {
    @include anchor-underline("off-hover", $body-color, $accent-color);
    transition: all 0.2s $ease-out-quad;

    @include media($min: $breakpoint-ms, $max: $breakpoint-l) {
      display: inline-block;
    }

    @include breakpoint(l) {
      margin-left: $gutter;
    }
  }
}

.review-header-text,
.review-header-information {
  display: block;

  @include breakpoint(m) {
    display: inline-block;
  }
}

.review-header-information {
  font-family: $body-font;
  font-size: em($h6-font-base, $h4-font-base);
  font-weight: $body-font-weight;
  color: $body-color;

  @include breakpoint(l) {
    float: right;
  }
}

//-- Review Items --//
.review-item-stars {
  margin-bottom: $gutter-third;

  .review-item-score,
  .rating-stars {
    display: inline-block;
    line-height: 1;
    vertical-align: middle;
  }

  .review-item-score {
    margin-left: $gutter-third;
  }
}

.review-item-title {
  margin-top: 1em;
  margin-bottom: $gutter-third;
}

.review-item-body {
  margin: 0;
}

//-- Review Modal --//
.input-stars-wrapper {
  position: relative;
  width: 100%;

  .form-inline-message {
    display: block;
    margin-top: 0.5em;
    text-align: center;
    white-space: nowrap;

    + .form-inline-message {
      display: none;
    }
  }
}

.input-stars {
  width: 106px;
  max-width: 100%;
  margin-right: auto;
  margin-left: auto;

  &.form-field-error {
    padding-bottom: 20px;
  }

  .star {
    float: right;
    width: $star-size-medium;
    height: $star-size-medium;
    margin: 0 2px;
    cursor: pointer;
    transition: fill 0.2s $ease-out-quad;
    fill: contrast($star-color, 80%);

    &:last-of-type {
      margin-left: 0;
    }

    &:first-of-type {
      margin-right: 0;
    }

    &:hover,
    &:hover ~ .star svg {
      fill: $star-color;
    }
  }

  > input:checked {
    ~ label svg {
      fill: $star-color;
    }
  }
}

//-- Animations --//
.review-item { transition: opacity 0.4s $ease-out-quad; }

/* scss-combine:components/product/quick-shop */
.modal-quick-shop {
  opacity: 0;
  transition: opacity 0.5s $ease-out-quad;

  .loaded & {
    opacity: 1;
  }

  .modal-content {
    padding: $gutter;
  }

  .product-column-left {
    margin-top: $modal-icon-size + $gutter-third;
  }

  @include breakpoint(ms) {
    .product-column-left {
      width: percentage(6 / 12);
      margin-top: 0;
    }

    .product-column-right {
      width: percentage(6 / 12);
    }
  }

  .product-title {
    font-size: $h3-font-size;
  }

  .product-description {
    @extend %tertiary-font;
    font-size: $h5-font-size;
    font-weight: get-font-weight($weight: "light");
  }

  .module-product-tabs {
    margin-top: $gutter;

    .layout-container {
      padding: 0;
    }
  }
}

.modal-wrapper {
  .loading-spinner {
    opacity: 1;
    transition: opacity 0.1s $ease-out-quad;
  }

  &.loaded .loading-spinner {
    opacity: 0;
  }
}

.quickshop-messages {
  @extend %clearfix;
  width: 100%;

  .alert {
    margin-bottom: $gutter;
    overflow: hidden;
  }
}

/* scss-combine:components/product-item */
//-- Product Item Top Section --//
@if($alternate-style-for-product-item) {
  .product-item-top {
    border: $gutter-half solid $product-page-background;
  }

  .product-item-details {
    padding-bottom: $gutter-half;
  }
}

.product-item-top {
  @extend %product-item-image;
}

.product-item-badges {
  @extend %product-item-badges;
}

//-- Item Actions --//
.product-item-rating,
.product-item-quickshop,
.product-item-compare,
.product-item-remove {
  @extend %meta-text-treatment;
  position: absolute;
  opacity: 0;
  transition: all 0.5s $ease-out-quad;
  backface-visibility: hidden;

  .product-item-top:hover & {
    opacity: 1;
  }

  .no-js & {
    display: none;
  }
}

.product-item-quickshop {
  @extend %item-overlay-button;
}

.product-item-remove {
  top: $gutter-half;
  right: $gutter-half;
  display: block;

  //scss-lint:disable QualifyingElement
  input[type=submit] {
    @extend %button-base, %button-secondary;
    display: block;
    padding: 0 $gutter-third / 2;
    font-size: $h3-font-size;
    line-height: 1;
  }

  .product-item-thumbnail:hover & {
    opacity: 1;
  }
}

.product-item-compare {
  top: calc(60% + 40px);
  display: block;
  width: 100%;
  font-size: em(12px, $body-font-size);
  text-align: center;

  @include breakpoint(s-down) {
    position: absolute;
    display: none;
  }

  .form-label { display: inline-block; }

  .form-label-text {
    font-size: 1em;
    letter-spacing: em(1px, 12px);
  }

  .form-checkbox + .form-label-text {
    &,
    &:hover {
      @include anchor-underline("on-hover", $body-color, $heading-color);
      display: inline-block;
      width: auto;
      color: $product-item-compare;
      transition: all 0.4s $ease-out-quad;
    }

    &:before {
      @include icon-plus($product-item-compare);
      position: relative;
      top: 1px;
    }
  }

  .form-checkbox:checked + .form-label-text {
    &,
    &:hover {
      color: $product-item-compare-alt;
    }

    &:before {
      @include icon-plus($product-item-compare-alt, transparent);
    }
  }

  .product-item-top:hover & {
    //scss-lint:disable NameFormat
    @if $alternate-product-item-size {
      top: calc(50% + 30px);
    } @else {
      top: calc(50% + 40px);
    }
  }
}

.product-item-rating {
  bottom: -$gutter-third;
  display: block;
  width: 100%;
  text-align: center;

  .product-item-top:hover & {
    bottom: $gutter-third;
    opacity: 1;
  }
}

//-- Product Item Information Section --//
.product-item-details {
  padding-top: $gutter-half;
  text-align: center;
}

.product-item-brand {
  display: block;
  margin: em(7px, $body-font-size) 0;
  color: $body-color;
}

.product-item-title {
  @extend %product-item-title;
}

.product-item-price {
  font-size: em(16px);
}

/* scss-combine:components/product-item-grid */
//-- Column Setup--//
.product-item {
  @extend %product-item;

  //-- 3 items across --//
  .collection-row-of-3 & {
    @extend %product-item-grid-3;
  }

  //-- 4 items across --//
  .collection-row-of-4 & {
    @extend %product-item-grid-4;
  }
}

.product-grid {
  @extend %clearfix;
  padding-top: gutter();
  padding-bottom: gutter();
}

/* scss-combine:components/rating-stars */

.rating-stars {
  &.small-stars {
    min-height: $star-size-small;
  }

  &.medium-stars {
    min-height: $star-size-medium;
  }

  &.large-stars {
    min-height: $star-size-large;
  }
}

.rating-star {
  display: inline-block;
  line-height: 1;
  vertical-align: middle;

  .small-stars & {
    width: $star-size-small;
    height: $star-size-small;
  }

  .medium-stars & {
    width: $star-size-medium;
    height: $star-size-medium;
  }

  .large-stars & {
    width: $star-size-large;
    height: $star-size-large;
  }
}

.rating-star-full {
  use {
    fill: $star-color;
  }
}

.rating-star-empty {
  use {
    fill: contrast($star-color, 75%);
  }
}

/* scss-combine:components/share-links */
.share-buttons {
  position: relative;

  &.tab-title { display: inline-block; }
}

.share-button-item,
.share-buttons-list-trigger {
  vertical-align: middle;
  background: $share-color-button-base;
  border-color: transparent;
  transition: all 0.5s $ease-out-quad;
}

.share-buttons-list-trigger {
  @extend %tab-link;
  color: $share-color-button;

  &.active {
    color: $share-color-active;

    use {
      fill: $share-color-active;
    }
  }

  &:hover use {
    fill: $share-color-active;
  }

  use {
    transition: fill 0.5s $ease-out-quad;
    fill: $share-color-default;
  }
}

.share-button-icon {
  position: absolute;
  top: 50%;
  left: em(12px);
  width: em(23px, $body-font-size);
  height: em(26px, $body-font-size);
  margin-top: em(-26px, $body-font-size) / 2;

  use {
    max-width: 100%;
    max-height: 100%;
    fill: $share-color-button;
  }

  @include breakpoint(ms) {
    left: em(-55px, $body-font-size) / 2;
  }
}

.share-button-text {
  margin-left: $gutter-half / 2;
}

.share-buttons-list {
  position: absolute;
  right: 0;
  z-index: map-get($zindex, body-menus);
  min-width: 200px;
  padding: 0;
  margin: 0;
  font-size: $body-font-size;
  transition: all 0.5s $ease-out-quad;

  @include breakpoint(s-down) {
    width: 100%;
  }

  li {
    padding: 0;
    margin: 0;
    list-style: none;
  }

  .show-list-top & {
    bottom: em(42px, $body-font-size); //12px font, 20px padding, 10px gap

    &.hidden {
      bottom: 0;
    }
  }

  .show-list-bottom & {
    top: calc(100% + 1px);

    &.hidden {
      top: 0;
    }
  }

  &.animating-out,
  &.hidden {
    height: 0;
    overflow: hidden;
    opacity: 0;
    visibility: hidden;
  }

  &.animating {
    overflow: hidden;
  }

  &.visible {
    opacity: 1;
  }
}

.share-button-item {
  display: block;
  padding: $share-button-padding;
  font-size: $body-font-size - 1px;
  line-height: 1;
  background: $share-color-button-accent;
  border-top-width: 0;
  border-bottom-width: 0;

  @include breakpoint(ms) {
    padding-left: $share-button-padding;
  }

  &:first-child {
    border-top-width: 1px;
  }

  &:last-child {
    border-bottom-width: 1px;
  }

  &:hover {
    background: contrast($share-color-button-accent, 5%);
  }

  svg,
  .share-button-text {
    display: inline-block;
    vertical-align: middle;
  }

  svg {
    width: 20px;
    height: 20px;
  }

  .share-button-text {
    line-height: em(20px, $body-font-size - 1px);
  }

  &.share-facebook use {
    fill: $share-color-facebook;
  }

  &.share-twitter use {
    fill: $share-color-twitter;
  }

  &.share-linkedin use {
    fill: $share-color-linkedin;
  }

  &.share-google use {
    fill: $share-color-google;
  }

  &.share-pinterest use {
    fill: $share-color-pinterest;
  }

  &.share-email use,
  &.share-print use,
  &.share-clipboard use {
    fill: $share-color-default;
  }

  .fb-like iframe { max-width: none; }
}

/* scss-combine:components/loading */
.loading {
  @extend %overlay;
  background-color: $loading-background;
}

// http://tobiasahlin.com/spinkit/
.loading-spinner {
  position: absolute;
  top: 50%;
  left: 50%;
  display: block;
  width: 64px;
  height: 64px;
  margin-top: -32px;
  margin-left: -32px;

  &:before,
  &:after {
    position: absolute;
    top: 0;
    left: 0;
    display: inline-block;
    width: 64px;
    height: 64px;
    background-color: $accent-color;
    border-radius: 50%;
    content: "";
    opacity: 0.6;
    animation: bounce 2s infinite ease-in-out;
  }

  &:after {
    animation-delay: -1s;
  }
}

@keyframes bounce {
  0%, 100% {
    transform: scale(0);
  }
  50% {
    transform: scale(1);
  }
}

/* scss-combine:components/modals */
.modal-overlay {
  @extend %overlay;
  background-color: rgba($modal-background-color, 0.85);
}

.modal-wrapper {
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: map-get($zindex, main-overlay) + 1;
  display: block;
  width: 100%;
  height: 100vh;
  box-sizing: content-box;
  backface-visibility: hidden;

  > .modal {
    width: 80%;
    max-width: $modal-max-width;
    max-height: 100%;
    margin-right: auto;
    margin-left: auto;
    overflow-y: auto;

    @include breakpoint(s-down) {
      max-height: 100%;
      padding: 0;
    }

    &.modal-quick-shop {
      max-width: 100%;
      transition: margin-top 0.4s $ease-out-quad;
    }
  }
}

.modal-close {
  position: absolute;
  top: $gutter;
  right: $gutter;
  display: inline-block;
  line-height: 1;

  .modal-close-icon {
    display: inline-block;
    width: $modal-icon-size;
    height: $modal-icon-size;
    vertical-align: middle;

    use {
      transition: fill 0.4s $ease-out-quad;
      fill: $modal-accent-color;
    }
  }

  .modal-close-label {
    display: none;
    color: $modal-accent-color;
    transition: color 0.4s $ease-out-quad;

    @include breakpoint(ms) {
      display: inline-block;
      margin-left: 5px;
      line-height: 1;
      vertical-align: middle;
    }
  }

  &:hover {
    .modal-close-label {
      color: $modal-accent-color-active;
    }

    .modal-close-icon {
      //scss-lint:disable NestingDepth
      use {
        fill: $modal-accent-color-active;
      }
    }
  }
}

.modal-content {
  position: relative;
  width: 100%;
  margin-right: auto;
  margin-left: auto;
  background: $modal-content-background;
  box-sizing: border-box;

  @include breakpoint(ms-down) {
    overflow-y: auto;
  }
}

.modal-header,
.modal-form-contents,
.modal-footer {
  padding: $modal-gutter;

  @include breakpoint(ms-down) {
    overflow-y: $modal-gutter / 2;
  }
}

.modal-header {
  text-align: center;
}

.modal-heading {
  margin-top: 0;

  &:only-child {
    margin-bottom: 0;
  }

  + p {
    margin-bottom: 0;
  }
}

.modal-form-contents {
  @if ($content-same-as-background == false) {
    border-top: 1px solid $content-border-color;
    border-bottom: 1px solid $content-border-color;
  } @else {
    padding-top: 0;
  }

  + .modal-form-contents {
    border-top: 0;

    @if ($content-same-as-background) {
      padding: ($modal-gutter / 2) $modal-gutter;
    }
  }
}

/* scss-combine:components/masthead */
.masthead-item {
  position: relative;
  width: 100%;
  height: 100%;
  min-height: 100%;
  margin: 0;
  background: {
    position: center;
    repeat: no-repeat;
    size: cover;
  }

  &,
  .masthead-item-content {
    background-color: $masthead-background;
  }

  .masthead-item-content {
    display: table-cell;
    width: 100%;
    padding: $gutter-half;
    text-align: center;
    vertical-align: middle;

    &.has-background-image {
      border-right: 10px solid $content-background;
      border-left: 10px solid $content-background;
    }
  }

  .masthead-item-info {
    display: table;
    width: 100%;
    padding: 0 5%;
    margin-right: auto;
    margin-left: auto;
    vertical-align: middle;
    transition: width 0.2s $ease-out-quad;

    @include breakpoint(ms) {
      width: percentage(10 / 12);
      padding: 0;
    }

    @include breakpoint(m) {
      width: percentage(9 / 12);
    }

    @include breakpoint(l) {
      width: percentage(8 / 12);
    }
  }
}

.masthead-item {
  min-height: map-get($masthead-height, xs);

  @include breakpoint(ms) {
    min-height: map-get($masthead-height, ms);
  }
}

.masthead-item-content {
  height: map-get($masthead-height, xs);

  @include breakpoint(ms) {
    height: map-get($masthead-height, ms);
  }
}

.masthead-item-title {
  @extend %as-h2;
  position: relative;
  display: inline-block;
  margin: 0;
  line-height: 1.2;
  color: $masthead-color;
  white-space: pre-wrap;

  @include breakpoint(s-down) {
    font-size: $h4-font-size;
  }

  @include breakpoint(ms) {
    padding: 0 $gutter-half;
  }
}

.masthead-item-text {
  @extend %tertiary-font;
  margin: 0;
  color: $masthead-color;
  text-align: center;

  @include breakpoint(s) {
    padding-right: $gutter-half;
    padding-left: $gutter-half;
  }

  @include breakpoint(m) {
    padding: 0 $gutter-half;

    &:before {
      display: block;
      width: 100px;
      height: 1px;
      margin: $gutter-half auto;
      font-size: 1px;
      line-height: 1px;
      border-bottom: 1px solid $masthead-color;
      content: "";
    }
  }
}

.masthead-item-link {
  @include button-with-arrow();
  display: inline-block;
}

/* scss-combine:components/search/header */
@include breakpoint(m) {
  .search-page {
    .collection-header { padding-bottom: 0; }
    .collection-navigation { padding-top: 0; }
    .collection-navigation-list li {
      padding-top: 0;
      padding-bottom: 0;
    }

    .tab-title .tab-link {
      padding-top: $gutter + $gutter-half;
      padding-bottom: $gutter + $gutter-half - 2px;
    }
  }
}

.search-tabs {
  border-bottom: 0;

  .tab-title { max-width: inherit; }

  .tab-link {
    display: inline-block;
    opacity: 1;

    @include breakpoint(ms-down) {
      padding-top: 0;
      padding-bottom: 0;
    }
  }
}

/* scss-combine:components/search/masthead */
//-- Masthead --//
.masthead-search {
  .masthead-item-content {
    border-right-width: 0;
    border-left-width: 0;
  }
}

.masthead-search-title {
  @extend %as-h4;
  margin-top: 0;
  margin-bottom: em($gutter-half, $h4-font-base);
  color: $masthead-color;
  text-align: center;

  @include breakpoint(m) {
    font-size: em(36px, $body-font-size);
  }

  + .masthead-search {
    padding-top: em($gutter-half);
  }
}

.masthead-form-toggle {
  @include smallcaps();
  font-size: em(12px, $body-font-size);
  color: $masthead-color;
  border-bottom: 1px solid $masthead-color;
  transition: all 0.4s $ease-out-quad;

  &:hover {
    color: $search-masthead-accent;
    border-bottom-color: $search-masthead-accent;
  }
}

.masthead-search-form {
  position: relative;
  width: calc(100% - #{$gutter});
  height: ($h5-font-size + em($gutter * 2.5));
  max-width: 800px;
  margin-right: auto;
  margin-left: auto;
  overflow: hidden;
  transition: all 0.3s $ease-out-quad;

  .form-field {
    display: block;
    width: 100%;
    padding-bottom: em($gutter);
    margin-top: em($gutter);
  }

  .form-input {
    @extend %as-h5, %secondary-font;
    padding: 0 map-get($search-search-icon, width) + $gutter-third $gutter-half 0;
    color: $masthead-color;
    background-color: transparent;
    border: 0;
    border-bottom: 1px solid $search-masthead-accent;

    @include breakpoint(m) {
      font-size: $h4-font-size;
    }

    &::placeholder {
      color: $masthead-color;
    }
  }

  .form-field-title {
    @extend %show-for-sr;
  }

  .form-field-invalid {

    .form-input {
      background-image: none;
    }

    .form-inline-message {
      position: absolute;
      bottom: - $gutter;
      left: map-get($search-search-icon, width) + $gutter-third;
      color: $masthead-color;
    }
  }

  .form-field-valid .form-input {
    background-image: none;
  }
}

.masthead-search-submit {
  position: absolute;
  top: $gutter;
  right: 0;
  display: inline-block;
  width: map-get($search-search-icon, width);
  height: map-get($search-search-icon, height);
  vertical-align: middle;
  background: transparent;
  border: 0;
}

.masthead-search-submit-icon {
  display: inline-block;
  max-width: 100%;
  max-height: 100%;
  vertical-align: middle;

  use {
    transition: fill 0.2s $ease-out-quad;
    fill: $masthead-color;
  }

  &:hover use {
    fill: $masthead-color;
  }
}

/* scss-combine:components/search/search-information */
.no-content-results {
  padding-right: 0;
  padding-bottom: $gutter;
  padding-left: 0;
  max-width: 100%;
  border-bottom: 1px solid $content-border-color;

  @include breakpoint(m-down) {
    padding-top: $gutter-half;
  }
}

.search-result-item-title {
  margin-top: 0;
  font-size: $h4-font-size;
}

.search-result-item-content {
  margin-bottom: 0;
}

.search-result-item-more {
  @extend %meta-text-treatment;
  @include smallcaps();
  font-size: em(12, $body-font-size);
  color: $body-color;
  border-bottom: 1px solid $body-color;
  transition: all 0.4s $ease-out-quad;

  &:hover {
    color: $body-color-alt;
    border-color: $body-color-alt;
  }

  &:before {
    display: block;
    width: 100%;
    margin-top: $gutter-third / 2;
    content: "";
  }
}


// -- Pages -- //
/* scss-combine:pages/home */
.home-container {
  padding-right: 0;
  padding-left: 0;
  border-top: 0;
}

.products-new,
.products-featured {
  padding-top: $gutter-double;
  padding-bottom: $gutter-double;
}

/* scss-combine:pages/blog */
//-- Main Blog Page --//
.blog-page {
  @extend %clearfix;

  .page-content {
    @include breakpoint(ms) {
      display: table;
      width: 100%;
      table-layout: fixed;
    }
  }
}

//-- Blog, Article Listing --//
.blog-column-articles .blog-item {
  @extend %clearfix;

  @include breakpoint(ms) {
    max-width: $max-width - 200px;
    margin: $gutter-double auto;

    &:first-child {
      margin-top: 0;
    }
  }

  &:last-child {
    margin-bottom: $gutter;
  }

  .blog-item-header,
  .blog-item-content {
    background-color: $content-background;
  }

  .blog-item-header {
    padding: 0;
  }

  .blog-item-content {
    @extend %tertiary-font;
    padding: $blog-gutter;

    @include breakpoint(m) {
      padding: $blog-gutter $blog-gutter * 2 $blog-gutter * 2;
    }
  }

  .blog-item-actions {
    @extend %primary-font;
  }
}

/* scss-combine:pages/blog-post */
//------ Blog Post ------//
.blog-post-page {
  @extend %clearfix;
}

//-- Page Banner --//
.blog-post-featured-image {
  @include aspect-ratio(12 4);
  display: block;
  background: 50% 50% transparent no-repeat;
  background-color: $content-background;
  background-size: cover;
}

//-- Blog Post Structure --//
.blog-post-share,
.blog-post-content,
.blog-post-header {
  @include breakpoint(s-down) {
    @extend %wrapper;
  }

  @include breakpoint(ms) {
    @include span(10);
    @include squish(1);
  }

  @include breakpoint(m) {
    @include span(8);
    @include squish(2);
  }

  @include breakpoint(l) {
    @include span(6);
    @include squish(3);
  }
}

//-- Post Header --//
.blog-post-header {
  padding-top: $blog-gutter;
}

.blog-post-caption {
  padding: $blog-gutter / 2 0;
  border-top: 1px solid $content-border-color;
  border-bottom: 1px solid $content-border-color;
}

.blog-post-title {
  padding-bottom: $blog-gutter / 2;
  margin: 0;
  font-size: $h2-font-size;
}

//-- Post Body Content --//
.blog-post-content {
  padding-top: $blog-gutter;
  padding-bottom: $blog-gutter;
}

.blog-post-share {
  padding-bottom: $blog-gutter;
}

/* scss-combine:pages/brands */

.brands-page-link {
  @extend %clearfix;
  text-align: center;

  @include breakpoint(ms) {
    text-align: left;
  }

  li {
    @include breakpoint(ms) {
      @include gallery(6 of 12);
    }

    @include breakpoint(m) {
      @include gallery(4 of 12);
    }
  }

  a {
    @extend %primary-font-bold;
    @include smallcaps();
    @include anchor-underline('on-hover', $heading-color, $heading-color);
    font-size: em(12px, $body-font-size);
    line-height: 1.15;
  }
}

/* scss-combine:pages/cart */

.cart-page {
  background: transparent;
}

.cart-empty {
  padding: $gutter-double $gutter;
  margin: 0;
}

.shipping-method-badge {
  display: inline-block;
  max-height: 100%;
  line-height: 1;
  vertical-align: middle;
}

/* scss-combine:pages/category */
.category-subheader{
  font-size: $h3-font-size;
}

/* scss-combine:pages/collections */
//-- Collections Footer / Header --//
.collection-footer,
.collection-navigation {
  display: table;
  width: 100%;
  padding-top: $gutter;
  padding-bottom: $gutter;
  vertical-align: middle;

  @include breakpoint(m-down) {
    padding-top: $gutter-half;
    padding-bottom: $gutter-half;

    > div:not(:empty) {
      margin-top: $gutter-half;

      &:first-child {
        margin-top: 0;
      }
    }
  }
}

//-- Collection Header --//
.collection-header {
  padding: 0 $gutter-half $gutter-half;
  background-color: $content-background;
  border-bottom: 1px solid $content-border-color;

  @include breakpoint(l) {
    padding: 0 $gutter $gutter;
  }
}

.collection-navigation {
  padding-bottom: 0;
}

.collection-navigation-list,
.collection-sortby {
  display: block;
  width: 100%;
  text-align: center;
  vertical-align: middle;
  table-layout: fixed;
}

@include breakpoint(m) {
  .collection-navigation-list {
    text-align: left;

    &,
    ~ .collection-sortby {
      display: table-cell;
      width: 50%;
    }
  }
}

//-- Collection Header: Header navigation (sub categories, search tabs) --//
.collection-navigation-list {
  @extend %collection-header-list;

  ul {
    display: flex;
    align-content: center;
    align-items: center;
    flex-flow: row wrap;
    justify-content: center;

    @include breakpoint(m) {
      align-content: flex-start;
      align-items: flex-start;
      justify-content: flex-start;
    }
  }

  li {
    display: inline-block;
    max-width: percentage(1 / 4);
    padding: $gutter-third;
    margin-right: $gutter-half;
    line-height: 1.1;
  }

  a {
    @extend %collection-header-list-item;

    &:hover {
      @extend %collection-header-list-item-accent;
    }
  }
}

//-- Collection Header: Sortby / Filters --//
.collection-sortby {
  position: relative;

  @include breakpoint(m) {
    text-align: right;
  }
}

.collection-toggle-facets {
  @extend %button-reset, %primary-font-bold;
  @include anchor-underline("on-hover", $body-color, $body-color-alt);
  @include smallcaps(false);
  font-size: em(12, $body-font-size);
  letter-spacing: 0.1em;
  color: $heading-color;

  @include breakpoint(s-down) {
    position: relative;
    display: inline-block;
    margin-bottom: $gutter-half;
  }

  svg {
    width: $body-font-size / 1.5;
    height: $body-font-size / 1.2;
    margin-right: 5px;
    margin-left: 4px;
  }

  &.toggle-icon .icon-increment-vertical {
    transform: scale(1, 0);
  }
}

.collections-sort-by-form {
  display: inline-block;
  margin-left: $gutter-third;

  @include breakpoint(s-down) {
    width: 100%;
    margin-left: 0;
  }

  .form-field-title {
    margin-right: $gutter-half;
    margin-bottom: 0;
    text-transform: uppercase;
  }

  .form-select-wrapper {
    width: auto;
    max-width: $input-max-width * (percentage(2 / 3) / 100%);
  }
}

//-- Collection Footer --//
.collection-footer {
  background-color: $collections-background;

  @include breakpoint(l) {
    padding-top: $gutter;
    padding-bottom: 0;
  }
}

//------ Collection Main Grid ------//
.no-content-results,
.collection-products {
  @extend %clearfix;
  padding-top: $gutter-double;
  padding-bottom: $gutter-double + $gutter;
  background-color: $collections-background;
}

.no-content-results p,
.no-product-results {
  @extend %as-h6;
  padding-bottom: $gutter;
  margin: 0;
  text-align: center;
}

//-- Collections Filter Column --//
.collection-filter {
  @include clearfix();
  display: none;
  background-color: $body-background;

  &.facets-open {
    display: block;
    padding: $gutter-half;
    margin-top: $gutter;

    @include breakpoint(l) {
      padding: $gutter-double $gutter;
    }
  }
}

.collection-facet {
  margin-top: $gutter;

  @include breakpoint(s-only) {
    @include gallery(6 of 12);

    &:nth-child(-n+2) {
      margin-top: 0;
    }
  }

  @include breakpoint(ms-only) {
    @include gallery(4 of 12);

    &:nth-child(-n+3) {
      margin-top: 0;
    }
  }

  @include breakpoint(m) {
    @include gallery(3 of 12);

    &:nth-child(-n+4) {
      margin-top: 0;
    }
  }

  @include breakpoint(m) {
    margin-top: 0;
  }
}

.collection-inside-column-heading,
.collection-facet-heading,
.collection-filter-heading {
  @extend %as-h6;
  margin: 0;
  color: $body-color;
}

//-- Facet Lists & 'Shop by' fallback --//
.collection-facet-list-wrapper,
.collection-facet-range,
.collection-facet-list,
.collection-link-list {
  padding-top: $gutter-half;
  padding-bottom: $gutter-half;
}

.collection-facet-list-item,
.collection-link-list-item {

  &.hidden {
    display: none;
  }

  &.visible {
    display: block;
  }
}

.collection-facet-list-separation a { @extend %secondary-link-style; }

.collection-facet-list-additional {
  display: none;
}

.collection-facet-list-toggle {
  @extend %secondary-link-style, %button-reset;

  .hidden { display: none; }
}

.collection-facet-range {

  .form-field {
    float: left;
    width: 50%;
    margin-bottom: $gutter-half;

    &.minimum {
      padding-right: $gutter-third;
    }

    &.maximum {
      padding-left: $gutter-third;
    }
  }

  .form-input {
    font-size: em(10, $body-font-size);
    background-color: $content-background;
  }

  .button {
    font-size: em(10, $body-font-size);

    + .button {
      margin-top: $gutter-third;
      margin-left: 0;
    }
  }
}

[data-faceted-search-facet] {
  @extend %primary-link-style, %primary-font-bold;
  position: relative;
  display: inline-block;
  vertical-align: middle;

  .rating-stars {
    display: inline-block;
  }

  .is-active & {
    @include anchor-underline('off-hover', $heading-color, $body-color);

    &:after {
      position: absolute;
      right: - $gutter-half;
      bottom: 0;
      text-transform: lowercase;
      content: "x";
      transition: opacity 0.5s $ease-out-quad;
    }

    &:hover:after {
      opacity: 0.5;
    }
  }
}

/* scss-combine:pages/contact */
.button-contact-submit {
  @extend %button-large;
}

/* scss-combine:pages/compare */
.compare-page .block-content {
  padding-top: $gutter;
  padding-bottom: $gutter;
  overflow: hidden;

  @include breakpoint(ms) {
    max-width: $max-width + 200px;
  }
}

.compare-content { overflow: hidden; }

.comparison-item {
  //scss-lint:disable NestingDepth
  position: relative;
  display: block;
  float: left;
  width: 100%;
  overflow: visible;

  @include breakpoint(s-down) {
    .comparison-item-detail:before,
    .comparison-item-detail:after { display: block; }

    &:first-child {
      .comparison-item-detail:first-child:before { display: none; }
    }
  }

  @include breakpoint(ms) {
    .row-of-2 &,
    .row-of-3 &,
    .row-of-4 & {
      @include gallery(6);
      padding-top: $gutter;
    }

    .row-of-1 &,
    .row-of-2 &,
    .row-of-3 &,
    .row-of-4 & {
      &:nth-child(-n+2) {
        padding-top: 0;
      }

      &:nth-child(2n+1) {
        .comparison-item-detail:before,
        .comparison-item-detail:after { display: block; }
      }

      &:nth-child(-2n+1) {
        .comparison-item-detail:first-child:before { display: none; }
      }
    }
  }

  @include breakpoint(l) {
    .row-of-1 &,
    .row-of-2 & {
      &:nth-child(-n+2) {
        padding-top: 0;
      }

      &:nth-child(2n+1) {
        .comparison-item-detail:before,
        .comparison-item-detail:after { display: block; }
      }

      &:nth-child(-2n+1) {
        .comparison-item-detail:first-child:before { display: none; }
      }
    }

    .row-of-3 &,
    .row-of-4 & {
      &:nth-child(2n+1) {
        .comparison-item-detail:before,
        .comparison-item-detail:after { display: none; }
      }
    }

    .row-of-3 & {
      @include gallery(4);

      &:nth-child(-n+3) {
        padding-top: 0;
      }

      &:nth-child(3n+1) {
        .comparison-item-detail:before,
        .comparison-item-detail:after { display: block; }
      }

      &:nth-child(-3n+1) {
        .comparison-item-detail:first-child:before { display: none; }
      }
    }

    .row-of-4 & {
      @include gallery(3);

      &:nth-child(-n+4) {
        padding-top: 0;
      }

      &:nth-child(4n+1) {
        .comparison-item-detail:before,
        .comparison-item-detail:after { display: block; }
      }

      &:nth-child(-4n+1) {
        .comparison-item-detail:first-child:before { display: none; }
      }
    }
  }
}

.comparison-item-detail {
  position: relative;
  height: auto;
  padding: $gutter-double 0 $gutter;
  transition: all 0.5s $ease-out-quad;

  p {
    margin-top: 0;
    margin-bottom: 0;
  }

  &.comparison-item-detail-flush { padding-top: $gutter; }

  &:before {
    position: absolute;
    top: 0;
    left: 0;
    display: none;
    width: calc(100% * 4 + #{$gutter * 4});
    border-top: 1px solid $content-border-color;
    content: "";
  }

  &:after {
    @include smallcaps();
    position: absolute;
    top: $gutter;
    left: 0;
    display: none;
    float: left;
    font-size: em(13);
    font-weight: 400;
    content: attr(data-comparison-label);

    @include breakpoint(s-only) { top: em(15, 13px); }
  }
}

.comparison-item-image { @extend %product-item-image; }

.comparison-item-badges { @extend %product-item-badges; }

.comparison-item-title {
  display: table;
  width: 100%;
  vertical-align: bottom;

  h1 { @extend %product-item-title; }
}

.comparison-price .price-rrp {
  margin-left: 5px;
  text-decoration: line-through;
}

.comparison-item-rating {

  .rating-stars,
  .rating-text {
    display: inline-block;
    vertical-align: middle;
  }

  .rating-text {  @include smallcaps(); }

  .rating-stars + .rating-text {
    margin-left: 8px;
  }
}

.compare-item-brand {
  @extend %primary-link-style;
  @include anchor-underline("off-hover", $heading-color, $heading-color);
}

.comparison-item-links {
  margin-top: $gutter;
}

.comparison-item-remove {
  @extend %primary-link-style;
  display: inline-block;
  margin-top: $gutter-half;
}

