    padding-top: $gutter-double;
  }
}

.fluid-width-video-wrapper {
  margin-bottom: 1em;

  &:last-of-type {
    margin-bottom: 0;
  }
}

/* scss-combine:pages/error */
.error-page {

  .page-content {
    padding-bottom: $gutter;
    text-align: center;
  }
}

/* scss-combine:pages/product */
//-- Main Product Page --//
.product-page {
  @extend %clearfix;

  .page-content {
    padding-bottom: 0;
  }
}

.product-details {
  width: 100%;
  padding-bottom: $product-gutter;
  background-color: $product-page-background;

  .modal-quick-shop & {
    background: $content-background;
  }

  @include breakpoint(ms) {
    display: table;
    table-layout: fixed;
  }

  .modal-quick-shop & {
    padding-bottom: 0;
  }
}

//-- Product Columns --//
.product-column-left {
  display: inline-block;
  width: 100%;
  padding: $product-gutter-half 0;

  @include breakpoint(ms) {
    display: block;
    float: left;
    width: percentage(6 / 12);
    height: 100%;
    padding-right: $product-gutter;
  }

  @include breakpoint(l) {
    width: percentage(8 / 12);
  }

  .modal-quick-shop & {
    padding-top: 0;
    padding-bottom: 0;
  }
}

.product-column-right {
  display: inline-block;
  width: 100%;
  text-align: center;

  @include breakpoint(ms) {
    display: block;
    float: left;
    width: percentage(6 / 12);
    height: 100%;
  }

  @include breakpoint(l) {
    width: percentage(4 / 12);
  }

  ul li,
  ol li {
    list-style-position: inside;
  }
}

//-- Inside-column elements --//
.product-column-section {
  border-bottom: 1px solid $content-border-color;

  header:last-child,
  &:last-child {
    border-bottom: 0;
  }
}

.product-column-header {
  padding: $product-gutter-half 0;
}

.product-column-heading {
  margin: 0;
  font-size: $h4-font-size;
}

.product-column-row {
  padding: $product-gutter-half 0;
  @if ($content-same-as-background == false) {
    border-bottom: 1px solid $content-border-color;
  }

  &:last-child {
    border-bottom: 0;
  }
}

[data-load-more-reviews] {
  position: absolute;
  left: 50%;
  transform: translateX(-50%);

  .product-column-row & { position: relative; }
}

.product-details-hidden { display: none; }

//-- Product Quantity Input --//
.product-quantity {
  display: inline-block;
  vertical-align: middle;

  .form-label-text {
    @extend %show-for-sr;
  }
}

//-- Product Cart Actions --//
.product-form-actions {
  display: inline-block;
  margin-top: -7px;
  margin-left: 7px;
  vertical-align: middle;

  .button {
    margin-top: 7px;
  }
}

.product-wishlist-form {
  padding: 0 $product-gutter;
}

.product-wishlist-button {
  @extend %button-reset, %secondary-link-style;

  svg {
    width: $body-font-size / 1.5;
    height: $body-font-size / 1.2;
    margin-right: 5px;
    fill: $accent-color;
  }
}

//-- Main Product Details --//
.product-brand {
  color: $body-color;

  + .product-title {
    margin: em($gutter-third, 24px) 0;
  }
}

.product-title {
  @extend %as-h2;
  margin: 0 0 em($gutter-third, 24px);
}

.product-price {
  font-size: $h4-font-size;
}

.product-share {
  @include breakpoint(ms) {
    float: right;
  }
}

.product-description {
  @extend %tertiary-font;
  display: none;

  &.loaded,
  .modal-quick-shop & {
    display: block;
  }

  &.loaded + .product-description-read-more {
    display: inline-block;
  }
}

.product-description-read-more {
  @extend %primary-link-style;
  display: none;
  margin-top: $gutter-half;
}

.products-related {
  padding-top: $gutter-double;
  padding-bottom: $gutter;
  border-top: 1px solid $content-border-color;
}

/* scss-combine:pages/subscribe */
.newsletter-page {
  padding-bottom: $gutter;
}

/* scss-combine:pages/search */

.search-results-information {
  @extend %wrapper;
}

/* scss-combine:pages/sitemap */
.sitemap {
  @extend %clearfix;
  @include container;
}

.sitemap-group {
  @include span(12 first);
  margin-top: $gutter-double;

  &:first-child {
    margin-top: 0;
  }

  @include breakpoint(m) {
    @include gallery(6);

    &:nth-child(-n+2) {
      margin-top: 0;
    }
  }

  @include breakpoint(l) {
    @include gallery(4);

    &:nth-child(-n+3) {
      margin-top: 0;
    }
  }

  ul {
    ul {
      margin-bottom: $gutter-half;
      margin-left: $gutter-half;
    }
  }

  li {
    margin: $gutter-third / 2 0;
  }

  a {
    color: inherit;
  }
}

.sitemap-title {
  @extend %as-h1;
  margin-top: 0;
  margin-bottom: 1em;
  font-size: $h5-font-size;
}

.sitemap-subsection {
  @extend %secondary-link-style;
}


//-- Account --//
/* scss-combine:account/account */
//-- Account Page Headings --//
.account-header {
  display: none;

  .account-page-orders-receipt &,
  .account-page-messages &,
  .account-page-addresses & {
    display: block;
  }

  .account-heading {
    display: none;
  }
}

.account-sub-heading {
  @extend %as-h3;
  margin-top: 0;
}

//-- Account Page Forms --//
.account-content {
  .form-select-wrapper {
    line-height: inherit;

    @include breakpoint(l) {
      max-width: 100%
    }
  }
}

//-- Account List Items --//
.account-item:first-of-type,
.account-page-add-address .account-content,
.account-page-settings .account-content {
  padding-top: 0;
  border-top-width: 0;

  > * {
    padding-top: 0;
  }

  .account-page-addresses & {
    border-top-width: 1px;

    > * {
      padding-top: $account-gutter / 2;
    }
  }
}

.account-receipt-summary {
  padding-right: 0;
}

//-- Account Product Items --//
.account-product-item {
  @extend %product-item;

  .collection-row-of-3 & {
    @extend %product-item-grid-3;
  }

  .collection-row-of-4 & {
    @extend %product-item-grid-4;
  }
}

.account-product-wrapper {
  @extend %clearfix;
  padding-bottom: gutter();
}

.account-product-item-thumbnail {
  @include aspect-ratio(map-get($product-item-image, width) map-get($product-item-image, height));
  position: relative;
  overflow: hidden;

  > a {
    @include aspect-ratio(map-get($product-item-image, width) map-get($product-item-image, height));
    display: block;
    overflow: hidden;
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: cover;
    transform: scale(1, 1);
    transition: all 0.5s $ease-out-quad;

    img {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      object-fit: cover;
      width: 100%;
    }
  }

  &:hover {
    > a {
      transform: scale(1.1, 1.1);
    }
  }
}

.account-product-item-sale-tag {
  @extend %subpixel-smooth;
  @extend %meta-text-treatment;
  position: absolute;
  top: 0;
  right: 0;
  display: inline-block;
  padding: 5px 10px;
  font-size: em(10, 14);
  color: $product-item-sale-color;
  pointer-events: none;
  background-color: $product-item-sale-background;
  transition: all 0.5s $ease-out-quad;
}

.account-product-item-remove {
  position: absolute;
  top: 0;
  left: 0;
  display: inline-block;

  input {
    @extend %button-base, %button-primary;
    padding: 5px 10px;
    font-size: em(10, 14);
  }
}

.account-product-item-details {
  padding-top: $gutter-half;
  text-align: center;
}

.account-product-item-brand {
  display: block;
  margin: em($gutter-third, $body-font-size) 0;
  color: $body-color;
}

.account-product-item-title {
  @extend %product-item-title;
}

.account-product-item-price {
  font-size: em(16px);
}

// Account Buttons
.account-page .form-actions,
.account-item-actions {
  .button {
    margin-right: em(15px);
    margin-left: 0;

    &:nth-child(even) {
      @extend %button-secondary;
    }

    &:last-child {
      margin-right: 0;
    }
  }
}

.account-button-secondary:not(.account-button-small),
.account-button-primary:not(.account-button-small) {
  @extend %button-large;
}

.account-nav{
  @extend %account-layout-nav;
}

.account-body {
  @extend %account-layout-body;
}

//-- Navigation --//
.account-nav-list {
  @extend %account-nav;
}
