pages/blog-post:
{{#partial "page"}}

  {{> components/breadcrumbs breadcrumbs}}

  <section class="page-container blog-page">
    <div class="page-content">
      <div class="blog-column-articles">

        <article class="blog-item">

          <header class="blog-item-header">
            {{#if blog.post.thumbnail}}
              <figure
                class="blog-item-featured-image"
                style="background-image: url({{getImage blog.post.thumbnail '1280x650'}})">
                <img class='show-for-sr' src="{{getImage blog.post.thumbnail '1280x650'}}" alt="{{blog.post.thumbnail.alt}}">
              </figure>
            {{/if}}

            <h4 class="blog-item-title">
              {{blog.post.title}}
            </h4>

            <div class="blog-item-header-caption">
              <time class="blog-item-posted-date">{{blog.post.date_published}}</time>

              {{#if blog.post.author}}
                <span class="blog-item-author">{{lang 'blog.posted_by' author=blog.post.author}}</span>
              {{/if}}
            </div>
          </header>

          <div class="blog-item-content rte">
            {{{blog.post.body}}}

            <div class="blog-item-actions">
              {{#if blog.post.tags}}
                <div class="blog-item-tags">
                  <span class="blog-item-tags-label">{{lang 'blog.tags'}}</span>
                  <ul class="blog-item-tags-list">
                    {{#each blog.post.tags}}
                      <li class="blog-item-tag">
                        <a href="{{url}}">#{{name}}</a>
                      </li>
                    {{/each}}
                  </ul>
                </div>
              {{/if}}

              {{> components/common/share-list
                settings.add_this
                cta=(lang 'blog.share_article')
                position='top'
                url=blog.post.url
                image=(getImage blog.post.thumbnail '1280x650' (cdn theme_settings.default_image))
              }}
            </div>
          </div>
        </article>

      </div>
    </div>
  </section>

  {{> core/structured-data/blog-post}}

{{/partial}}

{{> layout/base}}

components/breadcrumbs:
{{#if this.length '>' 2}}
  <div class="layout-container">
    <ol class="page-breadcrumbs">
      {{#each this}}
        <li class="breadcrumb-item">
          {{#unless @first}}
            {{# if name}}
              <span class="breadcrumb-separator">/</span>
            {{/if}}
          {{/unless}}

          {{#all url name}}
            <a class="breadcrumb-label breadcrumb-link" href="{{url}}">
              <span>{{name}}</span>
            </a>
          {{else}}
            {{# if name}}
              <span class="breadcrumb-label {{#if @last}}is-active{{/if}}">{{{limit name 1500}}}{{#if name.length '>' 150}}&hellip;{{/if}}</span>
            {{/if}}
          {{/all}}
        </li>
      {{/each}}
    </ol>
  </div>
{{/if}}

core/structured-data/blog-post:
{{!-- Blog Post: include in blog-post.html page template --}}

<script id="schema-blog">
  var postDate = '{{blog.post.date_published}}';
  postDate = postDate.split(' ');
  postDate = postDate[0].replace(/\D/g, '');
  postDate = new Date(postDate);
  try {
    postDate = postDate.toISOString();
  }
  catch(error) {
    postDate = '{{blog.post.date_published}}';
  }

  var schema = document.createElement('script');
  schema.type = 'application/ld+json';
  schema.text = JSON.stringify({
    "@context": "http://schema.org",
    "@type": "BlogPosting",
    "mainEntityOfPage": {
      "@type": "WebPage",
      "@id": "{{blog.post.url}}"
    },
    "headline": "{{blog.post.title}}",
    "image": {
      "@type": "ImageObject",
      "url": "{{getImage blog.post.thumbnail core-blog-thumbnail}}",
      "height": 800,
      "width": 800
    },
    "datePublished": postDate,
    "author": {
      "@type": "Person",
      "name": "{{blog.post.author}}"
    },
     "publisher": {
      "@type": "Organization",
      "name": "{{settings.store_name}}",
      "logo": {
        "@type": "ImageObject",
        "url": "{{getImage settings.store_logo.image}}",
        "width": 600,
        "height": 60
      }
    },
    "description": "{{blog.post.title}}"
  });

  document.querySelector('body').appendChild(schema);

  var script = document.getElementById('schema-blog')
  script.parentElement.removeChild(script);
</script>

