components/footer/payment-icons:
{{#if theme_settings.enable-payment-icons}}
  <ul class="footer-payment-icons">
    {{#if theme_settings.payment-visa}}
      <li>
        <svg class="footer-payment-icon" role="img">
          <use xlink:href="#icon-visa"></use>
        </svg>
      </li>
    {{/if}}

    {{#if theme_settings.payment-mastercard}}
      <li>
        <svg class="footer-payment-icon" role="img">
          <use xlink:href="#icon-mastercard"></use>
        </svg>
      </li>
    {{/if}}

    {{#if theme_settings.payment-american-express}}
      <li>
        <svg class="footer-payment-icon" role="img">
          <use xlink:href="#icon-american-express"></use>
        </svg>
      </li>
    {{/if}}

    {{#if theme_settings.payment-discover}}
      <li>
        <svg class="footer-payment-icon" role="img">
          <use xlink:href="#icon-discover"></use>
        </svg>
      </li>
    {{/if}}

    {{#if theme_settings.payment-paypal}}
      <li>
        <svg class="footer-payment-icon" role="img">
          <use xlink:href="#icon-paypal"></use>
        </svg>
      </li>
    {{/if}}

    {{#if theme_settings.payment-apple-pay}}
      <li>
        <svg class="footer-payment-icon" role="img">
          <use xlink:href="#icon-apple-pay"></use>
        </svg>
      </li>
    {{/if}}
  </ul>
{{/if}}

svg/icon-increment:
{{#unless hide-wrapper}}
  <svg
    {{#if role}}role="{{role}}"{{/if}}
    version="1.1"
    xmlns="http://www.w3.org/2000/svg"
    xmlns:xlink="http://www.w3.org/1999/xlink"
    viewBox="0 0 32 32">
{{/unless}}
<path fill-rule="evenodd" class="icon-increment-vertical" d="M19.037 3.544h-6.076v24.911h6.076v-24.911z"></path>
<path fill-rule="evenodd" class="icon-increment-horizontal" d="M3.999 13.266v5.468h24v-5.468h-24z"></path>
{{#unless hide-wrapper}}
  </svg>
{{/unless}}
