components/category/sidebar:
{{#if category.faceted_search_enabled}}
  {{#each category.facets}}
    {{#if partial '!==' 'range'}}
      {{#if items}}
        {{> components/faceted-search/facets-base}}
      {{/if}}
    {{else}}
      {{> components/faceted-search/facets-base}}
    {{/if}}
  {{/each}}
{{else}}
  {{> components/common/collections/shop-by-brand }}
  {{> components/common/collections/shop-by-price shop_by_price=category.shop_by_price category_url=category.url}}
{{/if}}

components/common/collections/shop-by-br:
{{#if shop_by_brand}}
  <div class="collection-facet">
    <h1 class="collection-inside-column-heading">
      {{lang 'collections.shop_by.brand'}}
    </h1>
    <ul class="collection-facet-list">
      {{#each (limit shop_by_brand 5)}}
        {{#if ../brand.name '!==' name}}
          <li class="collection-facet-list-item">
            <a href="{{url}}" data-faceted-search-facet data-go-to-url>{{name}}</a>
          </li>
        {{/if}}
      {{/each}}

      {{#if shop_by_brand.length '>' 5}}
        <li class="collection-facet-list-item collection-facet-list-separation visible">
          <a href="{{urls.brands}}">{{lang 'footer.brands.view_all'}}</a>
        </li>
      {{/if}}
    </ul>
  </div>
{{/if}}

components/common/collections/shop-by-pr:
{{#if shop_by_price}}
  <div class="collection-facet">
    <h1 class="collection-inside-column-heading">
      {{lang 'collections.shop_by.price'}}
    </h1>
    <ul class="collection-facet-list">
      {{#each shop_by_price}}
        <li class="collection-facet-list-item {{#if selected}}is-active{{/if}}">
          <a
            data-faceted-search-facet
            href="{{#if selected}}{{../../category_url}}{{else}}{{url}}{{/if}}"
            title="{{low.formatted}} - {{high.formatted}}">
            {{low.formatted}} - {{high.formatted}}
          </a>
        </li>
      {{/each}}
    </ul>
  </div>
{{/if}}
